public boolean modified()
{
    boolean isModified, isValid;

    isModified = super();

    if (isModified)
    {
        isValid = budgetAnalysisInquiryHelper.setDatesByInterval(this.valueStr());
        if (isValid)
        {
            ctrlFromDate.dateValue(budgetAnalysisInquiryHelper.parmStartDate());
            ctrlToDate.dateValue(budgetAnalysisInquiryHelper.parmEndDate());
        }
        else
        {
            this.text('');
        }
    }

    return isModified;
}

