public int selectionChange()
{
    int selectionChanged;

    selectionChanged = super();

    if ((selectionChanged) && (this.valueStr() != budgetAnalysisInquiryHelper.parmFilterDimensionAttribute()))
    {
        ctrlFilterDimensionValue.text(nullValue(filterDimensionAttributeValue));
        budgetAnalysisInquiryHelper.parmFilterDimensionAttributeValue(ctrlFilterDimensionValue.valueStr());
        budgetAnalysisInquiryHelper.parmFilterDimensionAttribute(this.valueStr());
    }

    return selectionChanged;
}

public void lookup()
{
    super();

    if (strLen(ctrlFilterDimension.valueStr()) > 0)
    {
        budgetAnalysisInquiryHelper.lookupDimensionAttributeValue(this);
    }
}

public boolean modified()
{
    boolean isModified;

    isModified = super();

    if (isModified)
    {
        budgetAnalysisInquiryHelper.parmFilterDimensionAttributeValue(this.valueStr());
    }

    return isModified;
}

public boolean validate()
{
    boolean isValid;

    isValid = super();

    if (isValid)
    {
        if ((strLen(this.valueStr()) > 0) && (!budgetAnalysisInquiryHelper.validateDimensionAttributeValueFilter(this.valueStr())))
        {
            isValid = checkFailed("@GLS150408");
        }
    }

    return isValid;
}

void clicked()
{
    super();

    budgetAnalysisInquiryHelper.initTreeControl(ctrlDimensionTree);
}

public boolean expanding(int _Idx, FormTreeExpand _action, anytype _data)
{
    boolean         ret;
    FormTreeItem    formTreeItem;

    ret = super(_idx, _action, _data);
    formTreeItem = this.getItem(_Idx);

    if (formTreeItem)
    {
        budgetAnalysisDimensionNode = formTreeItem.data();

        if ((!budgetAnalysisDimensionNode.parmIsLeafNode())
            && (budgetAnalysisDimensionNode.parmChildMap().empty())
            && (!formTreeItem.stateExpanded())
            && (!formTreeItem.stateExpandedOnce()))
        {
            budgetAnalysisInquiryHelper.insertTreeNodes(this, _Idx);
        }
    }
    return ret;
}

public void selectionChanged(FormTreeItem _oldItem, FormTreeItem _newItem, FormTreeSelect _how)
{
    super(_oldItem, _newItem, _how);

    selectedNode = ctrlDimensionTree.getItem(ctrlDimensionTree.getSelection());
    budgetAnalysisDimensionNode = selectedNode.data();
    ctrlSelectedNode.text(budgetAnalysisDimensionNode.getNodeText());

    element.getBudgetSummary(this);
}

