[Form]
public class BudgetControlDataMaintenance extends FormRun implements BudgetControlIDataMaintenanceDesigner
{
    #task

    private List overrunSupportedProviders;
    private List selectionChangedProviders;
    private List orderByProviders;

}

public void init()
{
    super();

    element.setFormState();
}

public int task(int _p1)
{
    int ret;

    ret = super(_p1);

    switch (_p1)
    {
        case #taskEditRecord:
        case #taskNew:
            element.setFormState();
            break;
    }

    return ret;
}

[Hookable(false)]
public FormDataSource getBudgetControlDataMaintenanceTrackingDataSource()
{
    return BudgetControlDataMaintenanceTracking_ds;
}

public void setFormState()
{
    int64 selectedDocumentCount = BudgetControlDataMaintenance::getSelectedDocumentCount(budgetControlDataMaintenance.RecId);

    linesRecordActions.enabled(!element.inViewMode() && budgetControlDataMaintenanceTracking.RecId && budgetControlDataMaintenance.ProcessStatus == BudgetControlDataMaintenanceStatus::NotStarted);

    selectScenariosButton.enabled(budgetControlDataMaintenance.ID && budgetControlDataMaintenance.ProcessStatus == BudgetControlDataMaintenanceStatus::NotStarted);
    processDocumentsButton.enabled(selectedDocumentCount > 0 && budgetControlDataMaintenance.id && budgetControlDataMaintenance.ProcessStatus == BudgetControlDataMaintenanceStatus::NotStarted);
    BudgetControlDataMaintenance_IsOverrunApproved.enabled(this.isOverrunApprovedSupported());

    budgetControlDataMaintenance_DS.allowEdit(budgetControlDataMaintenance.ProcessStatus == BudgetControlDataMaintenanceStatus::NotStarted);
    budgetControlDataMaintenance_DS.allowDelete(budgetControlDataMaintenance.ProcessStatus == BudgetControlDataMaintenanceStatus::NotStarted);
    budgetControlDataMaintenanceTracking_DS.allowEdit(budgetControlDataMaintenance.ProcessStatus == BudgetControlDataMaintenanceStatus::NotStarted);
    budgetControlDataMaintenanceTracking_DS.allowDelete(budgetControlDataMaintenance.ProcessStatus == BudgetControlDataMaintenanceStatus::NotStarted);
}

private void initializeBudgetControlDataMaintenance()
{
    element.initializeProviders();
    element.updateOrderBys();
}

private void initializeProviders()
{
    this.overrunSupportedProviders = new List(Types::Class);
    this.selectionChangedProviders = new List(Types::Class);
    this.orderByProviders = new List(Types::Class);

    Set providerClassNames = BudgetControlDataMaintenance::getProviderNames(budgetControlDataMaintenance.RecId);
    SetEnumerator setEnumerator = providerClassNames.getEnumerator();

    while (setEnumerator.moveNext())
    {
        var currentProvider = BudgetControlDataMaintenanceProvider::getProviderFromName(setEnumerator.current());

        if (currentProvider)
        {
            BudgetControlIDataMaintenanceOverrunSupported overrunSupportedProvider = currentProvider as BudgetControlIDataMaintenanceOverrunSupported;
            if (overrunSupportedProvider)
            {
                overrunSupportedProviders.addEnd(overrunSupportedProvider);
            }

            BudgetControlIDataMaintenanceTrackingSelected trackingSelectedProvider = currentProvider as BudgetControlIDataMaintenanceTrackingSelected;
            if (trackingSelectedProvider)
            {
                selectionChangedProviders.addEnd(trackingSelectedProvider);
            }

            BudgetControlIDataMaintenanceOrderByUpdatable orderByUpdatable = currentProvider as BudgetControlIDataMaintenanceOrderByUpdatable;
            if (orderByUpdatable)
            {
                orderByProviders.addEnd(orderByUpdatable);
            }
        }
    }
}

private void updateOrderBys()
{
    if (orderByProviders)
    {
        ListEnumerator listEnumerator = orderByProviders.getEnumerator();
        while (listEnumerator.moveNext())
        {
            BudgetControlIDataMaintenanceOrderByUpdatable orderByUpdatable = listEnumerator.current();
            if (orderByUpdatable)
            {
                orderByUpdatable.updateOrderBy(this);
            }
        }
    }
}

private boolean isOverrunApprovedSupported()
{
    boolean isOverrunSupported = false;

    if (overrunSupportedProviders)
    {
        ListEnumerator listEnumerator = overrunSupportedProviders.getEnumerator();

        while (listEnumerator.moveNext() && !isOverrunSupported)
        {
            BudgetControlIDataMaintenanceOverrunSupported overrunSupportedProvider = listEnumerator.current();
            if (overrunSupportedProvider)
            {
                isOverrunSupported = overrunSupportedProvider.isOverrunSupported();
            }
        }
    }

    return isOverrunSupported;
}

private void onSelectionChanged()
{
    boolean doesAnyRequireResearch = false;

    if (selectionChangedProviders)
    {
        ListEnumerator listEnumerator = selectionChangedProviders.getEnumerator();

        while (listEnumerator.moveNext())
        {
            BudgetControlIDataMaintenanceTrackingSelected trackingSelectedProvider = listEnumerator.current();
            if (trackingSelectedProvider)
            {
                if (trackingSelectedProvider.onSelectionChanged(budgetControlDataMaintenanceTracking))
                {
                    doesAnyRequireResearch = true;
                }
            }
        }
    }

    if (doesAnyRequireResearch)
    {
        budgetControlDataMaintenanceTracking_ds.research(true);
    }
}

public void delete()
{
    super();

    element.setFormState();
}

public void create(boolean _append = false)
{
    super(_append);

    element.setFormState();
}

public int active()
{
    int ret;

    ret = super();

    element.initializeBudgetControlDataMaintenance();
    element.setFormState();

    return ret;
}

public void init()
{
    super();

    // Filter the data maintenance records based on the current primary ledger.
    QueryBuildRange qbr_PrimaryLedger = this.queryBuildDataSource().addRange(fieldnum(BudgetControlDataMaintenance, PrimaryLedger));
    qbr_PrimaryLedger.value(queryValue(Ledger::current()));
    qbr_PrimaryLedger.status(RangeStatus::Hidden);

    //If budget control is disabled or inactive then data maintenance form should not open and throw exception
    if (!BudgetControlConfigurationManager::isBudgetControlEnabledAndConfigured())
    {
        throw error("@Budget:BudgetControlDataMaintainenceCannotBeOpened");
    }
}

public int active()
{
    int ret;

    ret = super();

    element.setFormState();

    return ret;
}

public void clicked()
{
    Args    args = new Args();
    FormRun dataMaintenanceDetectionForm;

    budgetControlDataMaintenance_ds.write();

    args.record(budgetControlDataMaintenance);

    dataMaintenanceDetectionForm = new MenuFunction(menuitemActionStr(BudgetControlDataMaintenanceDetection), MenuItemType::Action).create(args);

    if (dataMaintenanceDetectionForm)
    {
        dataMaintenanceDetectionForm.run();
        dataMaintenanceDetectionForm.wait();
    }

    budgetControlDataMaintenanceTracking_ds.research(true);

    element.initializeBudgetControlDataMaintenance();
}

public void clicked()
{
    BudgetControlDataMaintenance::setSelectedForExecutionGroup(budgetControlDataMaintenance.RecId, true);

    budgetControlDataMaintenanceTracking_ds.research();
}

public void clicked()
{
    BudgetControlDataMaintenance::setSelectedForExecutionGroup(budgetControlDataMaintenance.RecId, false);

    budgetControlDataMaintenanceTracking_ds.research();
}

public void clicked()
{
    Args args = new Args();
    MenuFunction menuFunction;

    args.record(budgetControlDataMaintenance);

    menuFunction = new MenuFunction(menuitemActionStr(BudgetControlDataMaintenanceProcess), MenuItemType::Action);

    if (menuFunction)
    {
        menuFunction.run(args);
    }

    budgetControlDataMaintenance_ds.research(true);
}

public void clicked()
{
    super();

    // Save the selection immediately so the setFormState method can read the state
    // of the records and set the form state appropriately.
    budgetControlDataMaintenanceTracking_ds.write();

    element.onSelectionChanged();

    element.setFormState();
}
