public class BudgetParameters extends FormRun
{
    boolean                     runExecuteDirect;
    TmpIdRef                    tmpIdRef;
    NumberSeqScope              scope;
    NumberSeqApplicationModule  numberSeqApplicationModule;
    container                   numberSequenceModules;
    #ISOCountryRegionCodes

}

/// <summary>
/// Enables or disables the create completed budget fields of the <c>BudgetParameters</c> table.
/// </summary>
/// <remarks>
/// The create completed budget fields are enabled only when the budget control is not enabled
/// on the primary ledger.
/// </remarks>
public void enableBudgetParameters()
{
    if (!Ledger::isLedgerBudgetControlEnabled() &&
        budgetParameters.CheckRevenueBudgetBalanceOption == BudgetCheckRevenueBalanceOption::None)
    {
        createCompletedBudgetTrans_IsProjectBudgetTransCreateAsCompleted.enabled(true);
        createCompletedBudgetTrans_IsAssetBudgetTransCreateAsCompleted.enabled(true);
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]) &&
            isConfigurationkeyEnabled(configurationKeyNum(RAsset)))
        {
            createCompletedBudgetTrans_IsAssetBudgetTransCreateAsCompleted_RU.enabled(true);
        }
        createCompletedBudgetTrans_IsDemandFcstBudgetTransCreateAsCompleted.enabled(true);
        createCompletedBudgetTrans_IsSupplyFcstBudgetTransCreateAsCompleted.enabled(true);
    }
    else
    {
        createCompletedBudgetTrans_IsProjectBudgetTransCreateAsCompleted.enabled(false);
        createCompletedBudgetTrans_IsProjectBudgetTransCreateAsCompleted.checked(false);
        budgetParameters.IsProjectBudgetTransCreateAsCompleted = false;

        createCompletedBudgetTrans_IsAssetBudgetTransCreateAsCompleted.enabled(false);
        createCompletedBudgetTrans_IsAssetBudgetTransCreateAsCompleted.checked(false);
        budgetParameters.IsAssetBudgetTransCreateAsCompleted = false;

        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]) &&
            isConfigurationkeyEnabled(configurationKeyNum(RAsset)))
        {
            createCompletedBudgetTrans_IsAssetBudgetTransCreateAsCompleted_RU.enabled(false);
            createCompletedBudgetTrans_IsAssetBudgetTransCreateAsCompleted_RU.checked(false);
            budgetParameters.IsAssetBudgetTransCreateAsCompleted_RU = false;
        }

        createCompletedBudgetTrans_IsDemandFcstBudgetTransCreateAsCompleted.enabled(false);
        createCompletedBudgetTrans_IsDemandFcstBudgetTransCreateAsCompleted.checked(false);
        budgetParameters.IsDemandFcstBudgetTransCreateAsCompleted = false;

        createCompletedBudgetTrans_IsSupplyFcstBudgetTransCreateAsCompleted.enabled(false);
        createCompletedBudgetTrans_IsSupplyFcstBudgetTransCreateAsCompleted.checked(false);
        budgetParameters.IsSupplyFcstBudgetTransCreateAsCompleted = false;
    }
}

void init()
{
    this.numberSeqPreInit();

    super();
    BudgetParameters::find();

    this.numberSeqPostInit();

    // Initialize regulatory checkbox enabled properties
    budgetParameters_ds.object(fieldNum(BudgetParameters, IsFrenchRegulatoryEnabled)).allowEdit(budgetParameters.IsBudgetReservationEnabled == NoYes::No);
    budgetParameters_ds.object(fieldNum(BudgetParameters, IsBudgetReservationEnabled)).allowEdit(budgetParameters.IsFrenchRegulatoryEnabled == NoYes::No);
}

public void numberSeqPostInit()
{
    ctrlAllowSameAs.visible(numberSeqApplicationModule.sameAsActive());
    ctrlLabelSameAs.visible(numberSeqApplicationModule.sameAsActive());
}

public void numberSeqPreInit()
{
    runExecuteDirect = false;
    numberSequenceModules = [NumberSeqModule::Budget];
    numberSeqApplicationModule = new NumberSeqModuleBudget();
    scope = NumberSeqScopeFactory::createDataAreaScope();
    NumberSeqApplicationModule::createReferencesMulti(numberSequenceModules, scope);
    tmpIdRef.setTmpData(NumberSequenceReference::configurationKeyTableMulti(numberSequenceModules));
}

void removeFilter()
{
    runExecuteDirect = false;
    numbersequenceReference_ds.executeQuery();
}

void executeQuery()
{
    if (runExecuteDirect)
    {
        super();
    }
    else
    {
        runExecuteDirect = true;
        this.queryRun(NumberSeqReference::buildQueryRunMulti(numberSequenceReference,
                                                             tmpIdRef,
                                                             numberSequenceTable,
                                                             numberSequenceModules,
                                                             scope));
        numbersequenceReference_ds.research();
    }
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (NumberSequenceDatatype::find(numberSequenceReference.NumberSequenceDatatype).DatatypeId == extendedTypeNum(BudgetTransactionId) &&
            numberSequenceReference.NumberSequenceId != 0)
        {
            // Validate the number sequence for Budget Transaction Number.
            ret = BudgetTransactionManager::checkBudgetTransactionNumberSequence(numberSequenceReference);
        }
    }

    return ret;
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    NumberSequenceCode code = _formReferenceControl.filterValue(
    AbsoluteFieldBinding::construct(fieldStr(NumberSequenceTable, NumberSequence),
    tableStr(NumberSequenceTable))).value();

    // Do not call super as we're providing our own disambiguation logic.

    return NumberSequenceTable::findByNaturalKey(code, scope.getId(true));
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Query                   lookupQuery;
    NumberSequenceTable     selectedRecord;
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(NumberSequenceTable), _formReferenceControl, true);

    // Display the NumberSequence field in the lookup form.
    sysTableLookup.addLookupfield(fieldNum(NumberSequenceTable, NumberSequence));

    // Create a custom Query that filters on NumberSequenceScope.
    lookupQuery = new Query();
    lookupQuery.addDataSource(tableNum(NumberSequenceTable)).addRange(fieldNum(NumberSequenceTable, NumberSequenceScope)).value(queryValue(scope.getId(true)));

    if (NumberSequenceDatatype::find(numberSequenceReference.NumberSequenceDatatype, false).DatatypeId == extendedTypeNum(BudgetTransactionId))
    {
        // The lookup is for budget transaction number. Only show number sequences that are continuous.
        lookupQuery.dataSourceTable(tableNum(NumberSequenceTable)).addRange(fieldNum(NumberSequenceTable, Continuous)).value(queryValue(NoYes::Yes));
    }

    sysTableLookup.parmQuery(lookupQuery);

    // Return the record selected by the user.
    selectedRecord = sysTableLookup.performFormLookup();

    return selectedRecord;
}

public int active()
{
    int ret;

    element.enableBudgetParameters();

    ret = super();

    return ret;
}

public void modified()
{
    super();

    element.enableBudgetParameters();
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (budgetParameters.IsAssetBudgetTransCreateAsCompleted)
    {
        // Disable other create completed fields only when this field is marked
        // otherwise unmark only this particular field.
        element.enableBudgetParameters();
    }

    return ret;
}

