public boolean validate()
{
    boolean ret;

    ret = super();

    if (budgetParameters.IsFrenchRegulatoryEnabled == NoYes::No && PurchCommitmentHeader_PSN::existCommitmentForCurrentFiscalYear())
    {
        if (Box::yesNo("@SYS4008233", DialogButton::No) == DialogButton::No)
        {
            ret = false;
        }
    }

    return ret;
}

