public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup          sysTableLookup = SysTableLookup::newParameters(tableNum(LedgerJournalName), _formControl);
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource = query.addDataSource(tableNum(LedgerJournalName));

    queryBuildDataSource.addRange(fieldNum(LedgerJournalName, JournalType)).value(int2str(enum2int(LedgerJournalType::Budget)));

    sysTableLookup.addLookupfield(fieldNum(LedgerJournalName, JournalName));
    sysTableLookup.addLookupfield(fieldNum(LedgerJournalName, JournalType));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public void pageActivated()
{
    super();

    element.enableBudgetParameters();
}

/// <summary>
/// Disable the budget reservation feature if the French regulatory feature is enabled.
/// </summary>
/// <returns> true, if the French Regulatory can been modified; otherwise, false. </returns>
public boolean modified()
{
    boolean isModified;

    isModified = super();

    if (isModified)
    {
        // Make sure the budget reservation feature is disabled if the French regulatory feature is enabled.
        budgetParameters_ds.object(fieldNum(BudgetParameters, IsBudgetReservationEnabled)).allowEdit(budgetParameters.IsFrenchRegulatoryEnabled == NoYes::No);
    }

    return isModified;
}

/// <summary>
/// Disable the French regulatory feature if the budget reservation feature is enabled.
/// </summary>
/// <returns> true, if the budget reservation can been modified; otherwise, false. </returns>
public boolean modified()
{
    boolean isModified;

    isModified = super();

    if (isModified)
    {
        // Make sure the French regulatory feature is disabled if the budget reservation feature is enabled.
        budgetParameters_ds.object(fieldNum(BudgetParameters, IsFrenchRegulatoryEnabled)).allowEdit(budgetParameters.IsBudgetReservationEnabled == NoYes::No);
    }

    return isModified;
}
