public class BudgetPlanDetailInquiry extends FormRun implements DimensionCombinationIPreview
{
    BudgetPlanDetailInquiryRequest budgetPlanDetailInquiryRequest;

}

public void init()
{
    #SysSystemDefinedButtons

    boolean                  canExecuteQuery;
    boolean                  addPreviewPaneRanges;
    FormCommandButtonControl showFiltersButton;

    budgetPlanDetailInquiryRequest = new BudgetPlanDetailInquiryRequest();

    if (element.args().parmObject() && element.args().parmObject() is BudgetPlanDetailInquiryRequest)
    {
        budgetPlanDetailInquiryRequest = element.args().parmObject() as BudgetPlanDetailInquiryRequest;
        canExecuteQuery = true;
        addPreviewPaneRanges = true;
    }
    else
    {
        budgetPlanDetailInquiryRequest.parmDimensionFocusName(DimensionHierarchy::find(BudgetPlanningConfiguration::find().DimensionHierarchy).Name);
        canExecuteQuery = budgetPlanDetailInquiryRequest.parmDimensionFocusName() != BudgetPlanningConstants::emptyString;
    }

    super();

    // Hide Filter pane button since parameter fasttab filters are unbound and cannot be displayed in Filter pane
    showFiltersButton = this.control(this.controlId(#SystemDefinedShowFiltersButton)) as FormCommandButtonControl;
    showFiltersButton.visible(false);

    if (canExecuteQuery)
    {
        if (addPreviewPaneRanges)
        {
            budgetPlanDetailInquiryRequest.addRangesForPreviewPane(BudgetPlanHeader_DS.query());
        }

        element.updateGridData();
        element.setFormControls();
    }

    this.setSubledgerButtonVisibility();
}

/// <summary>
/// Hide or show the subledger function buttons based on the user's security access to the associated menu items.
/// </summary>
public void setSubledgerButtonVisibility()
{
    MenuFunction menuFunction = new MenuFunction(menuItemDisplayStr(ForecastPurch), MenuItemType::Display);

    if (menuFunction)
    {
        GoToSupplyForecast.visible(menuFunction.checkAccessRights());
    }

    menuFunction = new MenuFunction(menuItemDisplayStr(ForecastSales), MenuItemType::Display);

    if (menuFunction)
    {
        GotoDemandForecast.visible(menuFunction.checkAccessRights());
    }

    if (FeatureStateProvider::IsFeatureEnabled(ProjForecastListPageV2Feature::instance()))
    {
        menuFunction = new MenuFunction(menuItemDisplayStr(ProjForecastListPageV2), MenuItemType::Display);
    }
    else 
    {
        menuFunction = new MenuFunction(menuItemDisplayStr(ProjForecastListPage), MenuItemType::Display);
    }

    if (menuFunction)
    {
        ProjectForecast.visible(menuFunction.checkAccessRights());
    }
}

public DimensionNameValueListContract getNameValueList(Common _previewSourceRecord)
{
    DimensionFocusNameTmp previewSourceDimensionFocusNameTmp = _previewSourceRecord as DimensionFocusNameTmp;
    DimensionFocusNameTmp previewDimensionFocusNameTmp;

    // Call the linkPhysicalTableInstance method to attach records to datasource.
    previewDimensionFocusNameTmp.linkPhysicalTableInstance(dimensionFocusNameTmp);

    // Select all fields from DimensionFocusNameTmp table as not all fields are part of form query.
    select firstonly * from previewDimensionFocusNameTmp
        where previewDimensionFocusNameTmp.FocusValue == previewSourceDimensionFocusNameTmp.FocusValue;

    if (!previewDimensionFocusNameTmp)
    {
        DimensionFocusNameTmp previewDimensionFocusNameDescriptionTmp;
        previewDimensionFocusNameDescriptionTmp.linkPhysicalTableInstance(dimensionFocusNameDescriptionTmp);

        select firstonly * from previewDimensionFocusNameTmp
            exists join previewDimensionFocusNameDescriptionTmp
                where previewDimensionFocusNameDescriptionTmp.FocusValue == previewSourceDimensionFocusNameTmp.FocusValue
                   && previewDimensionFocusNameDescriptionTmp.LedgerDimension == previewDimensionFocusNameTmp.LedgerDimension;
    }

    return DimensionFocusNameTmp::getFocusNameValueList(previewDimensionFocusNameTmp, budgetPlanDetailInquiryRequest.parmDimensionFocusName());
}

/// <summary>
/// Updates the data in the details grid.
/// </summary>
public void updateGridData()
{
    if (budgetPlanDetailInquiryRequest.validateParameters())
    {
        // Populate the dimensionFocusNameTmp table and call the linkPhysicalTableInstance method to attach records to datasource.
        dimensionFocusNameTmp.linkPhysicalTableInstance(budgetPlanDetailInquiryRequest.populateDimensionFocusNameTmp());
        dimensionFocusNameDescriptionTmp.linkPhysicalTableInstance(budgetPlanDetailInquiryRequest.getDimensionFocusNameDescriptionTmp());

        budgetPlanDetailInquiryRequest.addRanges(BudgetPlanHeader_DS.query());
        budgetPlanDetailInquiryRequest.addGroupByToQuery(BudgetPlanHeader_DS.query());

        // Execute the query.
        BudgetPlanHeader_DS.executeQuery();

        budgetPlanDetailInquiryRequest.showGroupByControl(element.design().controlName(budgetPlanDetailInquiryRequest.parmGroupByFieldName()));
    }
}

/// <summary>
/// Sets the values of the form controls using the object of the <c>BudgetPlanDetailInquiryRequest</c> instance passed by the form's caller.
/// </summary>
public void setFormControls()
{
    BudgetPlanScenarioControl.text(BudgetPlanScenarioTranslation::getLocalizedName(budgetPlanDetailInquiryRequest.parmBudgetPlanScenarioRecId()));
    DimensionFocus.text(budgetPlanDetailInquiryRequest.parmDimensionFocusName());
    BudgetingOrganization.value(budgetPlanDetailInquiryRequest.parmBudgetingOrganizationRecId());
    BudgetPlanDocumentNumber.value(budgetPlanDetailInquiryRequest.parmBudgetPlanHeaderRecId());
    BudgetPlanningProcessControl.value(budgetPlanDetailInquiryRequest.parmBudgetPlanningProcessRecId());
    DimensionFocus.text(budgetPlanDetailInquiryRequest.parmDimensionFocusName());
}

/// <summary>
/// Initializes the <c>BudgetPlanLineDetailInquiryRequest</c> instance to pass it to the caller.
/// </summary>
/// <param name = "_dimensionAttributeValueMap">
/// The value of the dimensionAttributeValueMap.
/// </param>
/// <returns>
/// An instance of the <c>BudgetPlanLineDetailInquiryRequest</c> class.
/// </returns>
public BudgetPlanLineDetailInquiryRequest initializeBudgetPlanLineDetailInquiryRequest(Map _dimensionAttributeValueMap)
{
    BudgetPlanLineDetailInquiryRequest budgetPlanLineDetailInquiryRequest = BudgetPlanLineDetailInquiryRequest::construct();

    budgetPlanLineDetailInquiryRequest.initFromBudgetPlanDetailInquiryRequest(budgetPlanDetailInquiryRequest);
    budgetPlanLineDetailInquiryRequest.parmDimensionAttributeValueSet(_dimensionAttributeValueMap);

    if (budgetPlanDetailInquiryRequest.parmGroupByFieldName())
    {
        budgetPlanLineDetailInquiryRequest.parmGroupByFieldName(budgetPlanDetailInquiryRequest.parmGroupByFieldName());
        budgetPlanLineDetailInquiryRequest.parmGroupByFieldValue(element.design().controlName(budgetPlanDetailInquiryRequest.parmGroupByFieldName()).valueStr());
    }

    return budgetPlanLineDetailInquiryRequest;
}

/// <summary>
/// Gets the value of the DimensionAttributeValueMap based on <c>LedgerDimension</c> field selected by the user on line.
/// </summary>
/// <returns>
/// A map of the dimensionAttributeValue.
/// </returns>
public Map getDimensionAttributeValueMap()
{
    Map                   dimensionAttributeValueMap = new Map(Types::String, Types::String);
    DimensionFocusNameTmp dimensionFocusNameTmpLocal;

    // Call the linkPhysicalTableInstance method to attach records to datasource.
    dimensionFocusNameTmpLocal.linkPhysicalTableInstance(dimensionFocusNameTmp);

    // Select all fields from DimensionFocusNameTmp table as not all fields are part of form query.
    select firstonly dimensionFocusNameTmpLocal
        where dimensionFocusNameTmpLocal.FocusValue == dimensionFocusNameTmp.FocusValue;

    DimensionHierarchyLevelCollection dimensionHierarchyLevelCollection = DimensionHierarchyLevelCollection::newForHierarchy(
        DimensionHierarchy::findByTypeAndName(
            DimensionHierarchyType::Focus,
            budgetPlanDetailInquiryRequest.parmDimensionFocusName()).RecId);

    dimensionHierarchyLevelCollection.initializeEnumerator();

    Name dimensionAttributeName;
    DimensionDisplayValue dimensionAttributeValue;

    while (dimensionHierarchyLevelCollection.moveNext())
    {
        dimensionAttributeName  = DimensionAttribute::find(dimensionHierarchyLevelCollection.currentDimensionAttributeId()).Name;
        dimensionAttributeValue = dimensionFocusNameTmpLocal.getFieldValue(strFmt("DimValue%1", dimensionHierarchyLevelCollection.currentLevel()));

        if (!dimensionAttributeValue)
        {
            dimensionAttributeValue = SysQuery::valueEmptyString();
        }

        dimensionAttributeValueMap.insert(dimensionAttributeName, dimensionAttributeValue);
    }

    return dimensionAttributeValueMap;
}

/// <summary>
/// Gets the value of the <c>MainAccount</c> field selected by the user on line.
/// </summary>
/// <returns>
/// The value of the <c>MainAccount</c> field.
/// </returns>
public DimensionDisplayValue getMainAccount()
{
    DimensionFocusNameTmp dimensionFocusNameTmpLocal;

    // Call the linkPhysicalTableInstance method to attach records to datasource.
    dimensionFocusNameTmpLocal.linkPhysicalTableInstance(dimensionFocusNameTmp);

    // Select all fields from DimensionFocusNameTmp table as not all fields are part of form query.
    select firstonly dimensionFocusNameTmpLocal
        where dimensionFocusNameTmpLocal.FocusValue == dimensionFocusNameTmp.FocusValue;

    return LedgerDimensionFacade::getMainAccountFromLedgerDimension(dimensionFocusNameTmpLocal.LedgerDimension).MainAccountId;
}

