    args.parmObject(element.initializeBudgetPlanLineDetailInquiryRequest(element.getDimensionAttributeValueMap()));
    budgetPlanLineDetailInquiryForm = new MenuFunction(this.menuItemName(), MenuItemType::Display).create(args);

    if (budgetPlanLineDetailInquiryForm)
