        budgetPlanLineDetailInquiryForm.run();
        budgetPlanLineDetailInquiryForm.wait();
    }
}

public void clicked()
{
    super();

    BudgetPlanSupplyForecastDrillThroughHelper budgetPlanSupplyForecastDrillThroughHelper = BudgetPlanSupplyForecastDrillThroughHelper::construct();
    BudgetPlanSupplyForecastDrillThroughHelper.parmMainAccountValue(element.getMainAccount());
    BudgetPlanSupplyForecastDrillThroughHelper.parmForecastModelId(BudgetPlanLineView.ForecastModelId);
    BudgetPlanSupplyForecastDrillThroughHelper.parmDimensionAttributeValueMap(element.getDimensionAttributeValueMap());

    BudgetPlanFormHelper::openDialogFormByMenuItem(
        MenuItemType::Display,
        menuItemDisplayStr(ForecastPurch),
