public void clicked()
{
    super();

    BudgetPlanProjectForecastDrillThroughHelper drillThroughHelper = BudgetPlanProjectForecastDrillThroughHelper::construct();

    drillThroughHelper.parmMainAccountValue(element.getMainAccount());
    drillThroughHelper.parmDimensionAttributeValueMap(element.getDimensionAttributeValueMap());

    switch (budgetPlanDetailInquiryRequest.parmGroupByFieldName())
    {
        case fieldStr(BudgetPlanLineView, ForecastModelId):
            drillThroughHelper.parmForecastModelId(BudgetPlanLineView.ForecastModelId);
            break;

        case fieldStr(BudgetPlanLineView, ProjectId):
            drillThroughHelper.parmProjectId(BudgetPlanLineView.ProjectId);
            break;
    }
    if (FeatureStateProvider::IsFeatureEnabled(ProjForecastListPageV2Feature::instance()))
    {
        BudgetPlanFormHelper::openDialogFormByMenuItem(
            MenuItemType::Display,
            menuItemDisplayStr(ProjForecastListPageV2),
            drillThroughHelper);
    }
    else 
    {
        BudgetPlanFormHelper::openDialogFormByMenuItem(
            MenuItemType::Display,
            menuItemDisplayStr(ProjForecastListPage),
            drillThroughHelper);
    }
}

