public class BudgetPlanGenerate extends FormRun
{
    BudgetPlanGenerateController    controller;
    BudgetPlanGenerateContract      contract;
    BudgetPlanGenerateForm          budgetPlanGenerateForm;
    SysOperationDialog              dialog;
}

/// <summary>
/// Check if the form can close.
/// </summary>
/// <returns>
/// true if the form can close; otherwise, false.
/// </returns>
public boolean canClose()
{
    boolean                    canClose;
    BudgetPlanGenerateService  budgetPlanGenerateService;

    canClose = super();

    if (canClose && element.closedOk())
    {
        changecompany (CompanyInfo::getDataArea(Ledger::primaryForLegalEntity(sourceLedger.value())))
        {
            canClose = controller.checkCloseDialog() && BudgetPlan::checkBudgetPlanNumberSequence();
        }

        if (canClose)
        {
            budgetPlanGenerateService = BudgetPlanGenerateService::newFromContract(budgetPlanGenerateForm.parmBudgetPlanGenerateContract());

            canClose = budgetPlanGenerateForm.canClose(this) && budgetPlanGenerateService.queryProducesBudgetPlanLines();

            if (canClose && !controller.batchInfo().parmBatchExecute())
            {
                canClose = budgetPlanGenerateService.promptForNonBatchRunOverride();
            }
        }
    }

    return canClose;
}

/// <summary>
/// Initializes the form.
/// </summary>
public void init()
{
    super();

    dialog = element.args().caller();
    controller = dialog.controller();

    controller.parmExecutionMode(SysOperationExecutionMode::Asynchronous);

    contract = controller.getDataContractObject();

    budgetPlanGenerateForm = BudgetPlanGenerateForm::newFromContract(contract);

    if (controller.parmArgs())
    {
        Common recordContext =  controller.parmArgs().record();

        if (recordContext.TableId == tableNum(BudgetPlanHeader))
        {
            budgetPlanGenerateForm.parmIsCalledFromBudgetPlan(true);
            budgetPlanGenerateForm.parmTargetBudgetPlanHeader(recordContext);
        }
    }

    budgetPlanGenerateForm.parmBudgetPlanGenerateController(controller);
    budgetPlanGenerateForm.initForm(this);
}

