public int selectionChange()
{
    BudgetPlanGenerateFormGenJour           budgetPlanGenerateFormGenJour;
    BudgetPlanGenerateFormBudgetPlan        budgetPlanGenerateFormBudgetPlan;
    BudgetPlanGenerateFormProjectForecast   budgetPlanGenerateFormProjectForecast;
    int selection;

    selection = super();

    if (budgetPlanGenerateForm is BudgetPlanGenerateFormGenJour)
    {
        budgetPlanGenerateFormGenJour = budgetPlanGenerateForm as BudgetPlanGenerateFormGenJour;
        selection = budgetPlanGenerateFormGenJour.modifiedIsHistoricalControl();
    }
    else if (budgetPlanGenerateForm is BudgetPlanGenerateFormBudgetPlan)
    {
        budgetPlanGenerateFormBudgetPlan = budgetPlanGenerateForm as BudgetPlanGenerateFormBudgetPlan;
        selection = budgetPlanGenerateFormBudgetPlan.modifiedIsHistoricalControl();
    }
    else if (budgetPlanGenerateForm is BudgetPlanGenerateFormProjectForecast)
    {
        budgetPlanGenerateFormProjectForecast = budgetPlanGenerateForm as BudgetPlanGenerateFormProjectForecast;
        selection = budgetPlanGenerateFormProjectForecast.modifiedIsHistoricalControl();
    }

    return selection;
}

