    HcmForecastScenario     hcmForecastScenarioFilter;

}

/// <summary>
/// Filters the forecast positions grid based upon the department selected by the user.
/// </summary>
public void applyDepartmentFilter()
{
    OMOperatingUnit omOperatingUnit = OMOperatingUnit::findName(filterDepartment.text(), OMOperatingUnitType::OMDepartment);

    hcmPositionForecastDetail_ds.queryBuildDataSource().clearRange(fieldNum(HcmPositionForecastDetail, Department));
    hcmPositionForecastDetailV2_ds.queryBuildDataSource().clearRange(fieldNum(HcmPositionForecastDetailV2, Department));

    if (filterDepartment.text() != '')
    {
        if (BudgetLinkPositionToMultipleForecastDetailsFlight::instance().isEnabled())
        {
            hcmPositionForecastDetailV2_ds.queryBuildDataSource().addRange(fieldNum(HcmPositionForecastDetailV2, Department)).value(queryValue(omOperatingUnit.RecId));
        }
        else
        {
            hcmPositionForecastDetail_ds.queryBuildDataSource().addRange(fieldNum(HcmPositionForecastDetail, Department)).value(queryValue(omOperatingUnit.RecId));
        }
    }

    hcmPositionForecastScenario_ds.executeQuery();
}

/// <summary>
/// Filters the forecast positions grid to remove positions that do not match the date selected by the user.
/// </summary>
public void applyModifiedDateFilter()
{
    hcmPositionForecastDetail_ds.queryBuildDataSource().clearRange(fieldNum(HcmPositionForecastDetail, CreatedDateTime));
    hcmPositionForecastDetailV2_ds.queryBuildDataSource().clearRange(fieldNum(HcmPositionForecastDetailV2, CreatedDateTime));

    if (filterModifiedDate.dateTimeValue() != utcDateTimeNull())
    {
        if (BudgetLinkPositionToMultipleForecastDetailsFlight::instance().isEnabled())
        {
            hcmPositionForecastDetailV2_ds.queryBuildDataSource().addRange(fieldNum(HcmPositionForecastDetailV2, CreatedDateTime)).value(queryValue(DateTimeUtil::date(filterModifiedDate.dateTimeValue())));
        }
        else
        {
            hcmPositionForecastDetail_ds.queryBuildDataSource().addRange(fieldNum(HcmPositionForecastDetail, CreatedDateTime)).value(queryValue(DateTimeUtil::date(filterModifiedDate.dateTimeValue())));
        }
    }

    hcmPositionForecastScenario_ds.executeQuery();
}

/// <summary>
/// Filters the forecast positions grid based on the budget plan scenario selected by the user.
/// </summary>
public void applyScenarioFilter()
{
    BudgetPlanHeader budgetPlanHeader = budgetPlanHcmSelectForm.parmBudgetPlanHeader();

    hcmForecastScenarioFilter = HcmForecastScenario::findByPlanningProcessAndScenarioName(budgetPlanHeader.BudgetPlanningProcess, filterBudgetScenario.text());
    hcmPositionForecastScenario_ds.executeQuery();
}

public void closeOk()
{
    BudgetPlanHeader                budgetPlanHeader = this.args().record() as BudgetPlanHeader;
    BudgetPlanningProcess           budgetPlanningProcess = BudgetPlanningProcess::find(budgetPlanHeader.BudgetPlanningProcess);
    MainAccount                     mainAccount;
    Ledger                          ledger = Ledger::find(budgetPlanningProcess.Ledger);
    boolean                         isValid = true;

    // We want to clear the main account value if the main account lookup is disabled.
    if (!mainAccountLookup.enabled())
    {
        mainAccount.clear();
    }
    else
    {
        mainAccount = MainAccount::findByMainAccountId(mainAccountLookup.text(), false, Ledger::find(budgetPlanningProcess.Ledger).ChartOfAccounts);
    }

    isValid = budgetPlanHcmSelectForm.validateCloseOk(mainAccount, ledger, budgetPlanningProcess, stagingBudgetPlanHcmPositionSelectTmp, mainAccountLookup.enabled());

    if (isValid)
    {
        super();

        budgetPlanHcmSelectForm.createBudgetPlanLines(
            stagingBudgetPlanHcmPositionSelectTmp,
            budgetPlanHeader,
            BudgetPlanScenario::findByName(BudgetPlanScenario.text()).RecId,
            BudgetPlanScenario::findByName(targetQuantityScenario.text()).RecId,
            LedgerDefaultAccountHelper::getDefaultAccountFromMainAccountId(mainAccount.MainAccountId));
    }
}

public void init()
{
    BudgetPlanHeader budgetPlanHeader;

    if (element.args().dataset() != tableNum(BudgetPlanHeader))
    {
        throw error(strFmt("@SYS19096", this.name(), tableStr(BudgetPlanHeader)));
    }
    else
    {
        budgetPlanHeader = this.args().record() as BudgetPlanHeader;
    }

    super();

    budgetPlanHcmSelectForm = new BudgetPlanHcmSelectForm();
    budgetPlanHcmSelectForm.parmBudgetPlanHeader(budgetPlanHeader);

    this.design().titleDatasource(budgetPlanHeader);
    headingRecordLabel.text(budgetPlanHcmSelectForm.getHeadingRecordLabelValue());

    // Initializing HcmForecastScenario
    hcmForecastScenarioFilter = HcmForecastScenario::findByPlanningProcessAndScenario(budgetPlanHeader.BudgetPlanningProcess, 0);
}

/// <summary>
/// Sets the enabled state of the select and remove buttons.
/// </summary>
public void setEnableSelectRemoveButtons()
{
    boolean isSelectionEnabled = !BudgetPlanScenario.text() || BudgetPlanScenario.text() == filterBudgetScenario.text();

    buttonSelect.enabled(isSelectionEnabled && hcmPositionForecast_ds.anyMarked());
    buttonRemove.enabled(stagingBudgetPlanHcmPositionSelectTmp_ds.anyMarked());
}

