public void clicked()
{
    HcmPositionForecastScenario positionForecastScenario;
    Set                         positionForecastScenarioCollection;

    super();

    positionForecastScenarioCollection = new Set(Types::Record);

    if (hcmPositionForecastScenario_ds.anyMarked())
    {
        // Get the references to the marked positions.
        positionForecastScenario = hcmPositionForecastScenario_ds.getFirst(true);

        while (positionForecastScenario)
        {
            positionForecastScenarioCollection.add(positionForecastScenario);
            positionForecastScenario = hcmPositionForecastScenario_ds.getNext();
        }
    }

    BudgetPlanHcmSelectForm::insertBudgetPlanHcmPositionSelectTmp(
        budgetPlanHcmPositionSelectTmp,
        stagingBudgetPlanHcmPositionSelectTmp,
        positionForecastScenarioCollection.pack());

    hcmPositionForecastScenario_ds.executeQuery();
    stagingBudgetPlanHcmPositionSelectTmp_ds.executeQuery();

    // Set the target budget plan scenario, if it was not already set.
    if (!BudgetPlanScenario.text())
    {
        BudgetPlanScenario.text(filterBudgetScenario.text());
    }
}

public void clicked()
{
    BudgetPlanHcmPositionSelectTmp budgetPlanPositionSelectTmp;
    Set                            positionForecastScenarioRecIdCollection;

    super();
    positionForecastScenarioRecIdCollection = new Set(Types::Int64);

    if (stagingBudgetPlanHcmPositionSelectTmp_ds.anyMarked())
    {
        // Get the references to the marked positions.
        budgetPlanPositionSelectTmp = stagingBudgetPlanHcmPositionSelectTmp_ds.getFirst(true);

        while (budgetPlanPositionSelectTmp)
        {
            positionForecastScenarioRecIdCollection.add(budgetPlanPositionSelectTmp.PositionForecastScenario);
            budgetPlanPositionSelectTmp = stagingBudgetPlanHcmPositionSelectTmp_ds.getNext();
        }
    }

    BudgetPlanHcmSelectForm::deleteBudgetPlanHcmPositionSelectTmp(
        budgetPlanHcmPositionSelectTmp,
        stagingBudgetPlanHcmPositionSelectTmp,
        positionForecastScenarioRecIdCollection.pack());

    hcmPositionForecastScenario_ds.executeQuery();
    stagingBudgetPlanHcmPositionSelectTmp_ds.executeQuery();
    stagingBudgetPlanHcmPositionSelectTmp_ds.markChanged();

    // Clear the target budget plan scenario, if the lower grid of selected positions is now empty.
    if (!stagingBudgetPlanHcmPositionSelectTmp.RecId)
    {
        BudgetPlanScenario.text('');
    }
}

public void lookup()
{
    budgetPlanHcmSelectForm.lookupQuantityScenario(this);
}

public boolean modified()
{
    boolean isModified;

    isModified = super();

    if (this.valueStr() == '')
    {
        mainAccountLookup.enabled(false);
        mainAccountLookup.mandatory(false);
    }
    else
    {
        mainAccountLookup.enabled(true);
        mainAccountLookup.mandatory(true);
    }

    return isModified;
}

public boolean validate()
{
    boolean isValid;

    isValid = super() && budgetPlanHcmSelectForm.validateQuantityScenario(this.text());

    return isValid;
}

public void lookup()
{
    BudgetPlanningProcess   budgetPlanningProcess = BudgetPlanningProcess::find(budgetPlanHcmSelectForm.parmBudgetPlanHeader().BudgetPlanningProcess);

    BudgetPlanHcmSelectForm::lookupMainAccount(this, budgetPlanningProcess.Ledger);
}

public boolean validate()
{
    BudgetPlanHeader        budgetPlanHeader = element.args().record() as BudgetPlanHeader;
    BudgetPlanningProcess   budgetPlanningProcess = BudgetPlanningProcess::find(budgetPlanHeader.BudgetPlanningProcess);
    Ledger                  ledger = Ledger::find(budgetPlanningProcess.Ledger);
    MainAccount             mainAccount = MainAccount::findByMainAccountId(this.text(), false, ledger.ChartOfAccounts);
    boolean isValid;

    isValid = super() && budgetPlanHcmSelectForm.validateMainAccount(mainAccount, ledger, budgetPlanningProcess);

    return isValid;
}
