[Form]
public class BudgetPlanLineDetailInquiry extends FormRun
{
    BudgetPlanLineDetailInquiryRequest budgetPlanLineDetailInquiryRequest;
    boolean executeQuery;

}

public void init()
{
    #SysSystemDefinedButtons

    FormCommandButtonControl showFiltersButton;

    executeQuery = false;

    if (element.args().parmObject()
        && element.args().parmObject() is BudgetPlanLineDetailInquiryRequest)
    {
        budgetPlanLineDetailInquiryRequest = element.args().parmObject();
        executeQuery = true;
    }
    else
    {
        budgetPlanLineDetailInquiryRequest = BudgetPlanLineDetailInquiryRequest::construct();
    }

    super();

    // Link the combination name temp table data source to the budget transaction line data source.
    DimensionCombinationNameTmp_Q.dataSourceTable(tableNum(DimensionCombinationNameTmp))
        .addLink(fieldNum(BudgetPlanLine, LedgerDimension),
            fieldNum(DimensionCombinationNameTmp, DimensionAttributeValueCombination));

    // Inform the DimensionCombinationPreviewPart of the location of the dimension foreign key field.
    DimensionCombinationNameTmp_Name.userDataItem(
        fieldNum(DimensionCombinationNameTmp, DimensionAttributeValueCombination));

    // Hide Filter pane button since parameter fasttab filters are unbound and cannot be displayed in Filter pane
    showFiltersButton = this.control(this.controlId(#SystemDefinedShowFiltersButton)) as FormCommandButtonControl;
    showFiltersButton.visible(false);

    if (executeQuery)
    {
        budgetPlanLineDetailInquiryRequest.addRanges(BudgetPlanLine_DS.query());

        if (budgetPlanLineDetailInquiryRequest.parmBudgetPlanLineActiveViewRecId())
        {
            budgetPlanLineDetailInquiryRequest.addRangesForPreviewPane(BudgetPlanLine_DS.query());
        }

        budgetPlanLineDetailInquiryRequest.addRangesForDimensionAttributes(BudgetPlanLine_DS.query());

        dimensionCombinationNameTmp.linkPhysicalTableInstance(
            budgetPlanLineDetailInquiryRequest.populateDimensionCombinationNameTmp());

        BudgetPlanLine_DS.executeQuery();
        this.setFormControls(budgetPlanLineDetailInquiryRequest);
    }
}

/// <summary>
/// Sets the values of the form controls using the object of the <c>BudgetPlanLineDetailInquiryRequest</c> instance passed by the form's caller.
/// </summary>
public void setFormControls(BudgetPlanLineDetailInquiryRequest _budgetPlanLineDetailInquiryRequest)
{
    BudgetPlanheader_BudgetingOrganization.value(_budgetPlanLineDetailInquiryRequest.parmBudgetingOrganizationRecId());
    BudgetPlanningProcessControl.value(_budgetPlanLineDetailInquiryRequest.parmBudgetPlanningProcessRecId());
    BudgetPlanDocumentNumber.value(_budgetPlanLineDetailInquiryRequest.parmBudgetPlanHeaderRecId());
    BudgetPlanScenarioControl.text(BudgetPlanScenarioTranslation::getLocalizedName(_budgetPlanLineDetailInquiryRequest.parmBudgetPlanScenarioRecId()));
    StartDate.dateValue(_budgetPlanLineDetailInquiryRequest.parmStartDate());
    EndDate.dateValue(_budgetPlanLineDetailInquiryRequest.parmEndDate());
}

public void init()
{
    super();

    // Populate the datasource to restrict the budget plan headers the user can view.
    budgetPlanInquiryTmp.linkPhysicalTableInstance(BudgetPlan::populateBudgetPlanInquiryTmp());
}

public void clicked()
{
    super();

    // Allow the dimension ranges to be edited.
    SysQueryRun sysQueryRun = new SysQueryRun(BudgetPlanLine_DS.query());
    sysQueryRun.promptAllowAddDataSource(false);
    sysQueryRun.promptShowSorting(false);
    sysQueryRun.promptLoadLastUsedQuery(false);
    sysQueryRun.title("@SYS75926");

    if (sysQueryRun.prompt())
    {
        BudgetPlanLine_DS.query(sysQueryRun.query());
    }
}

public boolean modified()
{
    boolean isModified = super();

    if (isModified)
    {
        budgetPlanLineDetailInquiryRequest.parmBudgetPlanningProcessRecId(this.value());
    }

    return isModified;
}

public Common lookupReference()
{
    Common budgetPlanHeaderLocal;

    if (budgetPlanLineDetailInquiryRequest.parmBudgetPlanningProcessRecId())
    {
        budgetPlanHeaderLocal = BudgetPlan::lookupBudgetPlanHeaderReference(this, budgetPlanLineDetailInquiryRequest.parmBudgetPlanningProcessRecId());
    }
    else
    {
        budgetPlanHeaderLocal = BudgetPlan::lookupBudgetPlanHeader(this);
    }

    return budgetPlanHeaderLocal;
}

