/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    // Add entities that are not identified by default.
    _menuOptions.dataEntityOptions().addEnd(OfficeMenuDataEntityOptions::construct(tableStr(BudgetPlanWorkflowStagesEntity)));
    _menuOptions.dataEntityOptions().addEnd(OfficeMenuDataEntityOptions::construct(tableStr(BudgetPlanLayoutEntity)));
}

public boolean canClose()
{
    boolean canClose;

    canClose = super()
               && BudgetPlanningWorkflowStage::validateWorkflowStage(budgetPlanningWorkflow.RecId);

    return canClose;
}

