/// <summary>
/// Initialize the form.
/// </summary>
public void init()
{
    SecurityRights securityRights = SecurityRights::construct();

    budgetPlanningWorkflowStagesListPanel = BudgetPlanningWorkflowStagesListPanel::newForm(element,
        element.controlId(formControlStr(BudgetPlanningConfiguration, BudgetListPanel)),
        securityRights.formControlAccessRight(formStr(BudgetPlanningConfiguration), formControlStr(BudgetPlanningConfiguration, BudgetListPanel)));

    if (element.args().parmEnumType() == enumNum(BudgetPlanningConfigurationType))
    {
        configurationType = element.args().parmEnum();

        element.initiateArgs();
    }

    super();

    BudgetPlanningConfiguration::find();

    budgetPlanningWorkflowStagesListPanel.init();
}

public void initiateArgs()
{
    BudgetPlanningWorkflowStage selectedWorkflowStage;

    switch (configurationType)
    {
        case BudgetPlanningConfigurationType::Workflow:
            if (element.args().lookupTable()
                && element.args().lookupTable() == tableNum(BudgetPlanningWorkflowStage))
            {
                selectedWorkflowStage = BudgetPlanningWorkflowStage::find(str2int64(element.args().lookupValue()));

                element.args().lookupTable(tableNum(BudgetPlanningWorkflow));
                element.args().lookupField(fieldNum(BudgetPlanningWorkflow, RecId));
                element.args().lookupValue(int642str(selectedWorkflowStage.BudgetPlanningWorkflow));
            }
            break;

        case BudgetPlanningConfigurationType::Column:
            if (element.args().lookupTable()
                && element.args().lookupTable() == tableNum(BudgetPlanElementDefinition))
            {
                element.args().lookupTable(tableNum(BudgetPlanColumn));
                element.args().lookupField(fieldNum(BudgetPlanColumn, RecId));
            }
            break;

        default:
            // Do nothing. Fall through.
            break;
    }
}

public void initiateSettings()
{
    switch (configurationType)
    {
        case BudgetPlanningConfigurationType::Parameters:
            Parameters.activatePage();
            break;

        case BudgetPlanningConfigurationType::Scenario:
            Scenarios.activatePage();
            BudgetPlanScenario currentScenario;
            currentScenario.data(budgetPlanScenario);
            budgetPlanScenario_DS.removeFilter();
            budgetPlanScenario_DS.positionToRecord(currentScenario);
            break;

        case BudgetPlanningConfigurationType::Stage:
            Stages.activatePage();
            BudgetPlanningStage currentStage;
            currentStage.data(budgetPlanningStage);
            budgetPlanningStage_DS.removeFilter();
            budgetPlanningStage_DS.positionToRecord(currentStage);
            break;

        case BudgetPlanningConfigurationType::Workflow:
            Workflows.activatePage();
            BudgetPlanningWorkflow currentWorkflow;
            currentWorkflow.data(budgetPlanningWorkflow);
            budgetPlanningWorkflow_DS.removeFilter();
            budgetPlanningWorkflow_DS.positionToRecord(currentWorkflow);
            break;

        case BudgetPlanningConfigurationType::AllocationSchedule:
            AllocationSchedule.activatePage();
            BudgetPlanningAllocationSchedule currentAllocationSchedule;
            currentAllocationSchedule.data(budgetPlanningAllocationSchedule);
            budgetPlanningAllocationSchedule_DS.removeFilter();
            budgetPlanningAllocationSchedule_DS.positionToRecord(currentAllocationSchedule);
            break;

        case BudgetPlanningConfigurationType::StageAllocation:
            StageAllocation.activatePage();
            BudgetPlanningStageAllocation currentStageAllocation;
            currentStageAllocation.data(budgetPlanningStageAllocation);
            budgetPlanningStageAllocation_DS.removeFilter();
            BudgetPlanningStageAllocation_DS.positionToRecord(currentStageAllocation);
            break;

        case BudgetPlanningConfigurationType::Priority:
            Priorities.activatePage();
            BudgetPlanPriority currentPriority;
            currentPriority.data(budgetPlanPriority);
            budgetPlanPriority_DS.removeFilter();
            budgetPlanPriority_DS.positionToRecord(currentPriority);
            break;

        case BudgetPlanningConfigurationType::Column:
            ColumnsTabPage.activatePage();
            BudgetPlanColumn currentColumn;
            currentColumn.data(budgetPlanColumn);
            budgetPlanColumn_DS.removeFilter();
            budgetPlanColumn_DS.positionToRecord(currentColumn);
            break;

        case BudgetPlanningConfigurationType::Layout:
            Layouts.activatePage();
            BudgetPlanLayout currentLayout;
            currentLayout.data(budgetPlanLayout);
            budgetPlanLayout_DS.removeFilter();
            budgetPlanLayout_DS.positionToRecord(currentLayout);
            break;

        case BudgetPlanningConfigurationType::AttachmentDirectory,
             BudgetPlanningConfigurationType::Template:
            // do nothing
            break;

        default:
            //throw an error when we have an undefined page tab
            throw error(Error::wrongUseOfFunction(funcName()));
    }
}

public void run()
{
    super();

    element.initiateSettings();
}

public void setEnabledAllocationSchedule()
{
    // Since we are also disabling the source scenario, so we need to call this method for every allocation method.
    element.setEnabledScenarioLookups();

    switch (budgetPlanningAllocationSchedule.AllocationMethod)
    {
        case BudgetPlanAllocateType::AllocateByPeriod:
            allocationScheduleGroup_M_editAllocationTerm.visible(false);
            allocationScheduleGroup_M_editLedgerAllocationRule.visible(false);
            allocationScheduleGroup_BasisPlanScenario.visible(false);
            allocationScheduleGroup_M_editLedgerAllocationKey.visible(true);
            allocationScheduleGroup_AncestorLevel.visible(false);
            budgetPlanningAllocationSchedule_ds.object(fieldNum(BudgetPlanningAllocationSchedule, UseSourceEffectiveDate)).visible(false);
            break;

        case BudgetPlanAllocateType::AllocateByDimension:
            allocationScheduleGroup_M_editAllocationTerm.visible(true);
            allocationScheduleGroup_M_editLedgerAllocationKey.visible(false);
            allocationScheduleGroup_M_editLedgerAllocationRule.visible(false);
            allocationScheduleGroup_BasisPlanScenario.visible(false);
            allocationScheduleGroup_AncestorLevel.visible(false);
            budgetPlanningAllocationSchedule_ds.object(fieldNum(BudgetPlanningAllocationSchedule, UseSourceEffectiveDate)).visible(false);
            break;

        case BudgetPlanAllocateType::AllocateByLedgerRule:
            allocationScheduleGroup_M_editAllocationTerm.visible(false);
            allocationScheduleGroup_M_editLedgerAllocationKey.visible(false);
            allocationScheduleGroup_M_editLedgerAllocationRule.visible(true);
            allocationScheduleGroup_AncestorLevel.visible(false);
            budgetPlanningAllocationSchedule_ds.object(fieldNum(BudgetPlanningAllocationSchedule, UseSourceEffectiveDate)).visible(true);
            break;

        case BudgetPlanAllocateType::AllocateByAncestor:
            allocationScheduleGroup_M_editAllocationTerm.visible(false);
            allocationScheduleGroup_M_editLedgerAllocationKey.visible(false);
            allocationScheduleGroup_M_editLedgerAllocationRule.visible(false);
            allocationScheduleGroup_BasisPlanScenario.visible(false);
            allocationScheduleGroup_AncestorLevel.visible(true);
            budgetPlanningAllocationSchedule_ds.object(fieldNum(BudgetPlanningAllocationSchedule, UseSourceEffectiveDate)).visible(false);
            break;

        default:
            allocationScheduleGroup_M_editAllocationTerm.visible(false);
            allocationScheduleGroup_M_editLedgerAllocationKey.visible(false);
            allocationScheduleGroup_M_editLedgerAllocationRule.visible(false);
            allocationScheduleGroup_BasisPlanScenario.visible(false);
            allocationScheduleGroup_AncestorLevel.visible(false);
            budgetPlanningAllocationSchedule_ds.object(fieldNum(BudgetPlanningAllocationSchedule, UseSourceEffectiveDate)).visible(false);
            break;
    }
}

