/// <summary>
/// Sets the availability of the source plan scenario lookup when <c>LedgerAllocationSource</c> enum is not set to <c>FixedValue</c> value.
/// Sets the availability of the basis plan scenario lookup when <c>BudgetPlanAllocateType</c> enum is set to <c>AllocateByLedgerRule</c> value.
/// </summary>
public void setEnabledScenarioLookups()
{
    LedgerAllocationRule ledgerAllocationRule;

    // Source scenario field is visible by default.
    allocationScheduleGroup_SourcePlanScenario.visible(true);

    // Basis scenario field is not visible by default.
    allocationScheduleGroup_BasisPlanScenario.visible(false);

    if (budgetPlanningAllocationSchedule.LedgerAllocationRule)
    {
        if (budgetPlanningAllocationSchedule.Ledger)
        {
            changecompany(CompanyInfo::getDataArea(Ledger::primaryForLegalEntity(budgetPlanningAllocationSchedule.Ledger)))
            {
                ledgerAllocationRule = LedgerAllocationRule::find(budgetPlanningAllocationSchedule.LedgerAllocationRule);

                if (ledgerAllocationRule.DataSource == LedgerAllocationSource::FixedValue)
                {
                    // If the user selected the rule with fixed value data source, source scenario field is emptied and not visible.
                    budgetPlanningAllocationSchedule.SourcePlanScenario = 0;
                    allocationScheduleGroup_SourcePlanScenario.visible(false);
                }

                if (ledgerAllocationRule.AllocationMethod == LedgerAllocationMethod::Basis)
                {
                    // If the user selected the basis rule, the basis scenario field is visible.
                    allocationScheduleGroup_BasisPlanScenario.visible(true);
                }
            }
        }
    }
}

/// <summary>
/// Sets the ordinal value based on the new positions of the <c>BudgetPlanLayoutElement</c> records.
/// </summary>
/// <param name = "_elementAtHigherPosition">The <c>BudgetPlanLayoutElement</c> position to move lower. </param>
/// <param name = "_elementAtLowerPosition"> The <c>BudgetPlanLayoutElement</c> position to move higher. </param>
public void setOrdinal(int _elementAtHigherPosition, int _elementAtLowerPosition)
{
    if (_elementAtLowerPosition && _elementAtHigherPosition)
    {
        BudgetPlanLayoutElement higherPositionRecord = BudgetPlanLayoutElement_DS.cursor(_elementAtHigherPosition);
        BudgetPlanLayoutElement lowerPositionRecord  = BudgetPlanLayoutElement_DS.cursor(_elementAtLowerPosition);

        BudgetPlanLayoutHelper::swapElementPosition(higherPositionRecord, lowerPositionRecord);

        BudgetPlanLayoutElement_DS.research(true);
    }
}

/// <summary>
/// Enables the add button on the layout element if a layout is present or if a layout is being added for which a value for both name and dimension hierarchy is present, else disables it.
/// </summary>
private void enableDisableLayoutElementAddButton()
{
    if (LayoutElementButtonGroup.enabled())
    {
        NewLayoutElement.enabled(budgetPlanLayout.RecId
                                 || (Header_BudgetPlanLayout_Name.valueStr() != emptyString && Header_BudgetPlanLayout_DimensionHierarchy.value() != 0));
    }
}

/// <summary>
/// Enables or disables various layout modifying actions and the template button.
/// </summary>
private void enableLayoutActions()
{
    this.setEditableLayoutState(!budgetPlanLayout.RecId || BudgetPlanLayoutHelper::canModifyLayout(budgetPlanLayout.RecId));
    this.setTemplateButton();
}

/// <summary>
/// Enables or disables editability of a layout and the layouts' layout elements.
/// </summary>
/// <param name = "_canEditLayout"> Boolean signifying that the current layout can be edited. </param>
private void setEditableLayoutState(boolean _canEditLayout)
{
    // Layout group
    ModifyLayout.visible(!_canEditLayout);
    BudgetPlanLayoutDescriptionElement.enabled(_canEditLayout);

    // Layout elements group
    BudgetPlanLayout_DS.object(fieldNum(BudgetPlanLayout, DimensionHierarchy)).allowEdit(_canEditLayout);
    BudgetPlanLayout_DS.object(fieldNum(BudgetPlanLayout, UseBudgetPlanLineMassUpdate)).allowEdit(!BudgetPlanLayout.UseBudgetPlanLineMassUpdate);
    BudgetPlanLayout_DS.object(fieldNum(BudgetPlanLayout, Description)).allowEdit(_canEditLayout);
    BudgetPlanLayoutElement_DS.allowDelete(_canEditLayout);
    BudgetPlanLayoutElement_DS.allowEdit(_canEditLayout);
    LayoutElementButtonGroup.enabled(_canEditLayout);
}

/// <summary>
/// Enables or disables the Template button.
/// </summary>
private void setTemplateButton()
{
    ExcelGroup.enabled(BudgetPlanLayoutHelper::canGenerateTemplateForLayout(budgetPlanLayout.RecId));
}

/// <summary>
