public void refreshRecord()
{
    budgetPlanLayout_ds.reread();
    budgetPlanLayout_ds.refresh();
}

/// <summary>
/// Sets the allowEdit and LookupOnly properties on the <c>RightSideValueOne</c> and <c>RightSideValueTwo</c> fields.
/// </summary>
public void setAllowEditAndLookupOnFieldValue()
{
    boolean fieldNamePresent = budgetPlanColumnRule.FieldName != emptyString;

    BudgetPlanColumnRule_RightSideValueOne.allowEdit(fieldNamePresent);
    BudgetPlanColumnRule_RightSideValueTwo.allowEdit(
        fieldNamePresent
        && (BudgetPlanColumnRule.Operator == BudgetPlanExpressionOperator::Between));
}

/// <summary>
/// Sets the allowEdit property for the <c>FieldName</c> field.
/// </summary>
public void setAllowEditOnFieldName()
{
    BudgetPlanColumnRule_FieldName.allowEdit(budgetPlanColumnRule.TableName != emptyString);
}

/// <summary>
/// Sets the ordinal value based on the new positions of the <c>BudgetPlanningStageAllocation</c> records.
/// </summary>
/// <param name = "_elementAtHigherPosition">The <c>BudgetPlanningStageAllocation</c> position to move lower.</param>
/// <param name = "_elementAtLowerPosition">The <c>BudgetPlanningStageAllocation</c> position to move higher.</param>
public void setStageAllocationOrdinal(int _elementAtHigherPosition, int _elementAtLowerPosition)
{
    BudgetPlanStageAllocationHelper::swapBudgetPlanningStageAllocationPriority(
        BudgetPlanningStageAllocation.BudgetPlanningWorkflowStage,
        _elementAtHigherPosition,
        _elementAtLowerPosition);

    BudgetPlanningStageAllocation_DS.research(true);
}

/// <summary>
/// Displays the year offset.
/// </summary>
[Wrappable(true)]
protected final void displayYearOffset()
{
    switch (BudgetPlanColumn_ColumnPeriodLength.selection())
    {
        case BudgetPlanColumnPeriodLength::Quarter,
            BudgetPlanColumnPeriodLength::Month,
            BudgetPlanColumnPeriodLength::Period:
            BudgetPlanColumn_YearOffset.visible(true);
            BudgetPlanColumn_ColumnPeriodLengthValue.visible(true);
            break;

        case BudgetPlanColumnPeriodLength::Year:
            BudgetPlanColumn_YearOffset.visible(true);
            BudgetPlanColumn_ColumnPeriodLengthValue.visible(false);
            break;

        default:
            this.defaultDisplayYearOffset();
            break;
    }
}

/// <summary>
/// Displays the default year offset.
/// </summary>
[Wrappable(true)]
protected final void defaultDisplayYearOffset()
{
    BudgetPlanColumn_YearOffset.visible(false);
    BudgetPlanColumn_ColumnPeriodLengthValue.visible(false);
}

/// <summary>
/// Updates the label of column period length.
/// </summary>
[Wrappable(true)]
protected final void updateColumnPeriodLengthValueLabel()
{
    switch (BudgetPlanColumn_ColumnPeriodLength.selection())
    {
        case BudgetPlanColumnPeriodLength::Month:
                BudgetPlanColumn_ColumnPeriodLengthValue
                    .label("@Budget:ColumnPeriodLengthValueMonthLabel");
            break;

        case BudgetPlanColumnPeriodLength::Period:
                BudgetPlanColumn_ColumnPeriodLengthValue
                    .label("@Budget:ColumnPeriodLengthValueFinancialPeriodLabel");
            break;

        case BudgetPlanColumnPeriodLength::Quarter:
                BudgetPlanColumn_ColumnPeriodLengthValue
                    .label("@Budget:ColumnPeriodLengthValueQuarterLabel");
            break;

        case BudgetPlanColumnPeriodLength::Year,
                 BudgetPlanColumnPeriodLength::All:
                // Do nothing in this case as the other control is not shown to the user
                break;

        default:
            var eventHandlerResult = EventHandlerResult::newSingleResponse();
            this.updateColumnPeriodLengthValueLabelDelegate(eventHandlerResult);

            if (eventHandlerResult.hasResult())
            {
                break;
            }

            throw error("@Budget:InvalidColumnPeriodLength");
    }
}

/// <summary>
/// Delegate performs default action on enumeration value of <c>BudgetPlanColumnPeriodLength</c> enum.
/// </summary>
/// <param name = "_result">
/// An <c>EventHandlerResult</c> instance.
/// </param>
/// <remarks>
/// To avoid the exception being thrown, a value must be returned using a delegate handler.
/// </remarks>
delegate void updateColumnPeriodLengthValueLabelDelegate(EventHandlerResult _result)
{
}

