public void clicked()
{
    boolean canModifyLayout = Box::confirm("@Budget:EditingLayoutWhenTemplateExistsWarning");
    if (canModifyLayout)
    {
        BudgetPlanTemplateManager::deleteTemplate(budgetPlanLayout.RecId);
        element.refreshRecord();
        element.setEditableLayoutState(true);
        element.enableDisableLayoutElementAddButton();
    }

    super();
}

public void clicked()
{
    SysTranslationHelper sysTranslationHelper = new SysTranslationHelper(tableNum(BudgetPlanLayout), tableNum(BudgetPlanLayoutTranslation));

    sysTranslationHelper.setMainTableNaturalKeyFieldId(fieldNum(BudgetPlanLayout, Name));

    sysTranslationHelper.launchTranslationListForm();

    super();
}

public void clicked()
{
    boolean doUpload = Box::confirm("@Budget:TemplateGenerationWillLockLayoutWarning");

    if (doUpload)
    {
        super();
        element.setEditableLayoutState(false);
        element.refreshRecord();
    }
}

public void clicked()
{
    super();

    BudgetPlanLayoutRecId selectedLayoutRecId = BudgetPlanLayout_DS.cursor().RecId;

    BudgetPlanLayoutHelper::refreshLayout(
        BudgetPlanMappingCacheDeleteType::DeleteMappingCacheByLayout,
        selectedLayoutRecId,
        BudgetPlanMappingRefreshType::RefreshByLayout,
        selectedLayoutRecId);

    BudgetPlanLayoutHelper::populateLayoutCache(selectedLayoutRecId);
}

