/// <summary>
/// Enable or disable form controls.
/// </summary>
/// <param name="_enable">
/// true to enable form controls; otherwise, false.
/// </param>
public void enableControls(boolean _enable = true)
{
    FormDataObject processDataObject;

    // Change the control enabled property only when it is different
    // from the control current setting.
    processDataObject = budgetPlanningProcess_ds.object(fieldNum(BudgetPlanningProcess, Name));
    if (processDataObject.allowEdit() != _enable)
    {
        processDataObject.allowEdit(_enable);
    }

    processDataObject = budgetPlanningProcess_ds.object(fieldNum(BudgetPlanningProcess, BudgetCycle));
    if (processDataObject.allowEdit() != _enable)
    {
        processDataObject.allowEdit(_enable);
    }

    processDataObject = budgetPlanningProcess_ds.object(fieldNum(BudgetPlanningProcess, BudgetPlanAttachmentDirectory));
    if (processDataObject.allowEdit() != _enable)
    {
        processDataObject.allowEdit(_enable);
    }

    processDataObject = budgetPlanningProcess_ds.object(fieldNum(BudgetPlanningProcess, BudgetPlanJustificationTemplate));
    if (processDataObject.allowEdit() != _enable)
    {
        processDataObject.allowEdit(_enable);
    }

    processDataObject = budgetPlanningProcess_ds.object(fieldNum(BudgetPlanningProcess, Ledger));
    if (processDataObject.allowEdit() != _enable)
    {
        processDataObject.allowEdit(_enable);
    }

    processDataObject = budgetPlanningProcess_ds.object(fieldNum(BudgetPlanningProcess, OrganizationHierarchyType));
    if (processDataObject.allowEdit() != _enable)
    {
        processDataObject.allowEdit(_enable);
    }

    processDataObject = budgetPlanningProcessAdministration_ds.object(fieldNum(BudgetPlanningProcessAdministration, BudgetingOrganization));
    if (processDataObject.allowEdit() != _enable)
    {
        processDataObject.allowEdit(_enable);
    }

    processDataObject = budgetPlanningProcessAdministration_ds.object(fieldNum(BudgetPlanningProcessAdministration, BudgetPlanningWorkflow));
    if (processDataObject.allowEdit() != _enable)
    {
        processDataObject.allowEdit(_enable);
    }

    processDataObject = budgetPlanningProcessAdministration_ds.object(fieldNum(BudgetPlanningStageRule, BudgetPlanningWorkflowStage));
    if (processDataObject.allowEdit() != _enable)
    {
        processDataObject.allowEdit(_enable);
    }

    processDataObject = budgetPlanningStageRule_ds.object(fieldNum(BudgetPlanningStageRule, CanAddChildBudgetPlans));
    if (processDataObject.allowEdit() != _enable)
    {
        processDataObject.allowEdit(_enable);
    }

    processDataObject = budgetPlanningStageRule_ds.object(fieldNum(BudgetPlanningStageRule, CanAddBudgetPlanLines));
    if (processDataObject.allowEdit() != _enable)
    {
        processDataObject.allowEdit(_enable);
    }

    processDataObject = budgetPlanningStageRule_ds.object(fieldNum(BudgetPlanningStageRule, CanModifyBudgetPlanLines));
    if (processDataObject.allowEdit() != _enable)
    {
        processDataObject.allowEdit(_enable);
    }

    processDataObject = budgetPlanningStageRule_ds.object(fieldNum(BudgetPlanningStageRule, Layout));
    if (processDataObject.allowEdit() != _enable)
    {
        processDataObject.allowEdit(_enable);
    }

    if (priorityListPanelGroup.enableChilds() != _enable)
    {
        priorityListPanelGroup.enableChilds(_enable);
    }
}

/// <summary>
/// Add the workflows associated with a budget planning process to the list view control.
/// </summary>
/// <returns>
/// A <c>Container</c> of budget planning workflow record IDs that are associated with a budget planning process.
/// </returns>
public container fillWorkflowListView()
{
    Counter                         counter;
    container                       workflowContainer;
    BudgetPlanningWorkflowRecId     budgetPlanningWorkflowRecId;

    workflowListView.deleteAll();

    // Add a column header if one didn't exist
    if (workflowlistview.getColumn(0) == null)
    {
        workflowListView.addColumn(0, new FormListColumn("@Budget:Workflow", 0));
    }

    // Get ordered list from BudgetPlanningWorkflow Table
    workflowContainer = BudgetPlanningWorkflow::getWorkflowsForBudgetPlanningProcess(budgetPlanningProcess.RecId);

    // initialize the listview control
    for (counter = 1; counter <= conLen(workflowContainer); counter++)
    {
        budgetPlanningWorkflowRecId = conPeek(workflowContainer, counter);

        workflowListView.add(BudgetPlanningWorkflow::find(budgetPlanningWorkflowRecId).Name);
    }

    return workflowContainer;
}

/// <summary>
/// Initialize the form.
