public void init()
{
    #resAppl

    SecurityRights securityRights = SecurityRights::construct();

    // Create the priority constraint list panel.
    budgetPlanPriorityConstraintListPanel = BudgetPlanPriorityConstraintListPanel::newForm(element,
        element.controlId(formControlStr(BudgetPlanningProcess, PriorityListPanelGroup)),
        securityRights.formControlAccessRight(formStr(BudgetPlanningProcess), formControlStr(BudgetPlanningProcess, PriorityListPanelGroup)));

    super();

    // Initialize the priority constraint list panel.
    budgetPlanPriorityConstraintListPanel.init();

    // Initialize the vertical splitter

    element.setProcessStateControls();
}

/// <summary>
/// Sets the budget planning approval state buttons Enabled property depending on the
/// approval state of the budget planning process.
/// </summary>
public void setProcessStateControls()
{
    switch (budgetPlanningProcess.ApprovalProcessState)
    {
        case BudgetPlanningApprovalProcessState::Draft:
            activateButton.enabled(true);
            deactivateButton.enabled(false);
            completeButton.enabled(false);
            element.enableControls(true);
            viewHierarchyButton.enabled(budgetPlanningProcess.RecId);
            break;

        case BudgetPlanningApprovalProcessState::InProcess:
            activateButton.enabled(false);
            deactivateButton.enabled(true);
            completeButton.enabled(true);
            element.enableControls(true);
            viewHierarchyButton.enabled(budgetPlanningProcess.RecId);
            break;

        default:
            activateButton.enabled(false);
            deactivateButton.enabled(false);
            completeButton.enabled(false);
            element.enableControls(false);
            viewHierarchyButton.enabled(budgetPlanningProcess.RecId);
            break;
    }
}

public boolean canClose()
