    return canClose;
}

/// <summary>
/// Re-populate the list panels when the <c>BudgetPlanningProcess</c> record changes.
/// </summary>
public void selectionChanged()
{
    super();

    // Add the workflows associated with the budget planning process to the list view panel control
    element.fillWorkflowListView();

    // Reset the approval process state buttons
    element.setProcessStateControls();

    // Fill the budget priority list panel
    budgetPlanPriorityConstraintListPanel.setBudgetPlanningProcess(budgetPlanningProcess.RecId);
    budgetPlanPriorityConstraintListPanel.fill();

    // Disable the fields when Budget planning process is activated
    budgetPlanningProcess_ds.object(fieldNum(BudgetPlanningProcess, Name)).allowEdit(budgetPlanningProcess.ApprovalProcessState == BudgetPlanningApprovalProcessState::Draft);
    budgetPlanningProcess_ds.object(fieldNum(BudgetPlanningProcess, Ledger)).allowEdit(budgetPlanningProcess.ApprovalProcessState == BudgetPlanningApprovalProcessState::Draft);
}

public edit DocuTemplateDisplayName editBudgetPlanTemplates(boolean _set, DocuTemplateDisplayName _name)
{
    DocuTemplate docuTemplate;
    BudgetPlanTemplate budgetPlanTemplate;
    DocuTemplateDisplayName displayName;

    if (_set)
    {
        budgetPlanningProcess.BudgetPlanJustificationTemplate = BudgetPlanTemplate::findByDocuTemplateDisplayName(_name).RecId;
        displayName = _name;
    }
    else
    {
        displayName = BudgetPlanTemplate::getDisplayNameByRecId(budgetPlanningProcess.BudgetPlanJustificationTemplate);
    }

    return displayName;
}

