public boolean validate()
{
    boolean isValid;
    boolean existsBudgetPlan;

    isValid = super();

    // Validates if the Budget planning organization hierarchy type previously exists.
    if (isValid
        && budgetPlanningProcess.orig().OrganizationHierarchyType != 0
        && BudgetPlanningProcessSetup::existsBudgetPlanForBudgetPlanningProcess(budgetPlanningProcess.RecId))
    {
        isValid = checkFailed("@Budget:BudgetPlanning_Validation_CannotChangeWorkflow");
    }

    if (isValid && !budgetPlanningProcess)
    {
        isValid = budgetPlanningProcess.validateWrite();
    }

    return isValid;
}

public void modified()
{
    // Need to save the budgetPlanningProcess if it is not already saved in the database because we need a valid
    // RecId to associate budgetPlanningProcessAdministration to it.
    if (budgetPlanningProcess.RecId == 0)
    {
        budgetPlanningProcess.insert();

        budgetPlanningProcess_DS.research(true);
        budgetPlanningProcess_DS.refresh();

        if (budgetPlanningProcess.OrganizationHierarchyType)
        {
            viewHierarchyButton.enabled(true);
        }
    }

    super();

    budgetPlanningProcessAdministration_ds.executeQuery();
}

