/// <summary>
/// Called when the dialog associated with the <c>BudgetPlanningProcessCopyDropDialog</c>
/// button is closed which adds the new budget planning process specified in the dialog.
/// </summary>
/// <param name="_formRun">
/// The dialog form which was closed.
/// </param>
public void dialogClosed(xFormRun _formRun)
{
    BudgetPlanningIProcess  formSourceBudgetPlanningProcess;
    BudgetPlanningProcess   budgetPlanningProcessCopy;

    super(_formRun);

    if (_formRun.closedOk())
    {
        formSourceBudgetPlanningProcess = _formRun as BudgetPlanningIProcess;

        if (formSourceBudgetPlanningProcess is BudgetPlanningIProcess)
        {
            budgetPlanningProcessCopy = formSourceBudgetPlanningProcess.parmBudgetPlanningProcess();

            BudgetPlanningProcessCopy::createBudgetPlanningProcessCopy(
                budgetPlanningProcessCopy.Name,
                budgetPlanningProcessCopy.BudgetCycle,
                budgetPlanningProcessCopy.Ledger,
                budgetPlanningProcessCopy.AccountStructure,
                budgetPlanningProcess.RecId);

            budgetPlanningProcess_ds.research(true);
        }
    }
}

public void clicked()
{
    int currentRecordPosition = 0;

    if (budgetPlanningProcess.validateWrite() && BudgetPlanningProcessSetup::activateProcess(budgetPlanningProcess.RecId))
    {
        super();

        BudgetPlanLineDefaultRowTemplateManager::generateAllDefaultRowTemplatesForProcess(budgetPlanningProcess);

        currentRecordPosition = budgetPlanningProcess_ds.getPosition();
        budgetPlanningProcess_ds.executeQuery();
        budgetPlanningProcess_ds.setPosition(currentRecordPosition);

        // Reset enable on process state controls
        element.setProcessStateControls();
    }
}

public void clicked()
{
    int currentRecordPosition = 0;

    if (BudgetPlanningProcessSetup::deactivateProcess(
            budgetPlanningProcess.Name,
            budgetPlanningProcess.ApprovalProcessState,
            budgetPlanningProcess.RecId))
    {
        super();

        currentRecordPosition = budgetPlanningProcess_ds.getPosition();
        budgetPlanningProcess_ds.executeQuery();
        budgetPlanningProcess_ds.setPosition(currentRecordPosition);

        // Reset enable on process state controls
        element.setProcessStateControls();
    }
}

public void clicked()
{
    if (BudgetPlanningProcessSetup::changeProcessState(budgetPlanningProcess.RecId, BudgetPlanningApprovalProcessState::Completed))
    {
        super();

        BudgetPlanLineDefaultRowTemplateManager::deleteByBudgetPlanningProcess(budgetPlanningProcess.RecId);

        budgetPlanningProcess_ds.reread();

        // Reset enable on process state controls
        element.setProcessStateControls();
    }
}

