public boolean validate()
{
    boolean isValid = super();
    boolean isEmptyValue = this.text() == BudgetPlanningConstants::emptyString;

    isValid = isValid && (isEmptyValue || BudgetPlanTemplate::findByDocuTemplateDisplayName(this.text()).RecId);

    if (!isValid)
    {
        warning(strFmt("@Budget:UnableToFindUniqueRecord", tablePName(BudgetPlanTemplate)));
    }

    return isValid;
}

public boolean modified()
{
    boolean isModified;

    if (this.value() != budgetPlanningProcess.OrganizationHierarchyType
        && (budgetPlanningProcess.OrganizationHierarchyType == 0
            ||Box::okCancel("@Budget:ModifiedOrganizationHierarchyWarning", DialogButton::Ok) == DialogButton::Ok))
    {
        isModified = super();
    }
    else
    {
        isModified = true;
    }

    return isModified;
}

public boolean modified()
