public class BudgetReservation_PSN extends FormRun implements BudgetReservationIRefreshable
{
    NumberSeqFormHandler numberSeqFormHandler;
    BudgetReservation_PSN budgetReservation;    
    ProjParameters projParameters;

}

public void refresh(Common _record = null)
{
    BudgetReservationHeader_PSN_ds.research();
    BudgetReservationLine_PSN_ds.research();

    if (_record
        && _record is BudgetReservationHeader_PSN)
    {
        BudgetReservationHeader_PSN_ds.positionToRecord(_record);

        this.setEditMode();
    }

}

private void setEditMode()
{
    #Task

    TabPageDetails.activatePage();
    element.task(#taskEditRecord);
    element.design().viewEditMode(ViewEditMode::Edit);
}

/// <summary>
/// Cancels the budget reservation document.
/// </summary>    
public void cancelDocument()
{   
    Args    args = new Args();
    FormRun cancelFormRun;

    args.name(formStr(BudgetReservationCancelDialog_PSN));
    args.caller(this);
    args.record(budgetReservationHeader_PSN);
    cancelFormRun = classfactory.formRunClass(args);
    cancelFormRun.init();
    cancelFormRun.run();
    cancelFormRun.wait(true);

    budgetReservationHeader_PSN_ds.research(true);
    budgetReservationLine_PSN_ds.research(true);

    this.setFormState();
}

