/// <summary>
/// Enables or disables the project sales price field based on whether the default cost price as sales price field is checked.
/// </summary>    
public void enableProjSalesPrice()
{
    boolean mustDefaultCostPriceAsSalesPrice = false;
    boolean isSalesCurrencyLocked = false;

    if (budgetReservationLine_PSN.ProjId)
    {
        if (budgetReservationLine_PSN.ProcurementCategory && projParameters.DefaultCostPriceAsSalesPrice)
        {
            mustDefaultCostPriceAsSalesPrice = true;
        }

        if (ProjInvoiceTable::isSalesCurrencyLocked(ProjTable::find(budgetReservationLine_PSN.ProjId).ProjInvoiceProjId))
        {
            isSalesCurrencyLocked = true;
        }

        budgetReservationLine_PSN_ds.object(fieldNum(BudgetReservationLine_PSN, ProjSalesCurrencyId)).allowEdit(!mustDefaultCostPriceAsSalesPrice && !isSalesCurrencyLocked);
        budgetReservationLine_PSN_ds.object(fieldNum(BudgetReservationLine_PSN, ProjSalesCurrencyId)).skip(mustDefaultCostPriceAsSalesPrice || isSalesCurrencyLocked);    
        budgetReservationLine_PSN_ds.object(fieldNum(BudgetReservationLine_PSN, ProjSalesPrice)).allowEdit(!mustDefaultCostPriceAsSalesPrice);
    }
}

public void init()
{
    super();

    budgetReservation = BudgetReservation_PSN::newFromBudgetReservationHeader_PSN(budgetReservationHeader_PSN);

    BudgetReservationLine_PSN_LedgerDimension.parmValidateBlockedForManualEntry(true);
    BudgetReservationLine_PSN_LedgerDimension.parmPostingType(LedgerPostingType::BudgetReservation_PSN);

    budgetReservationLine_PSN_DS.object(fieldNum(BudgetReservationLine_PSN, LedgerDimension)).mandatory(true);    

    projParameters = ProjParameters::find();        

    QueryBuildDataSource queryBudgetReservationHeader;

    queryBudgetReservationHeader = BudgetReservationHeader_PSN_ds.queryBuildDataSource();
    BudgetReservationHeader_PSN_ds.query().addQueryFilter(queryBudgetReservationHeader, fieldstr(BudgetReservationHeader_PSN, DocumentNumber));
    BudgetReservationHeader_PSN_ds.query().addQueryFilter(queryBudgetReservationHeader, fieldstr(BudgetReservationHeader_PSN, DocumentTitle));
    BudgetReservationHeader_PSN_ds.query().addQueryFilter(queryBudgetReservationHeader, fieldstr(BudgetReservationHeader_PSN, BudgetReservationType_PSN));
    BudgetReservationHeader_PSN_ds.query().addQueryFilter(queryBudgetReservationHeader, fieldstr(BudgetReservationHeader_PSN, StartDate));
}

