/// <summary>
/// Initializes and returns the <c>NumberSeqFormHandler</c> object for the budget reservation.
/// </summary>
/// <returns>
/// Returns the <c>NumberSeqFormHandler</c>.
/// </returns>    
public NumberSeqFormHandler numberSeqFormHandler()
{
    if (!numberSeqFormHandler)
    {
        numberSeqFormHandler = NumberSeqFormHandler::newForm(
            BudgetParameters::numRefBudgetReservation_PSN().NumberSequenceId,
            element,
            budgetReservationHeader_PSN_ds,
            fieldNum(BudgetReservationHeader_PSN, DocumentNumber));
    }

    return numberSeqFormHandler;
}

/// <summary>
/// Performs an on demand budget check on the current budget reservation line.
/// </summary>    
public void performBudgetCheck()
{
    if (budgetReservationLine_PSN)
    {
        budgetReservation.performBudgetCheckOnLine(budgetReservationLine_PSN);
        budgetReservationLine_PSN_ds.updateBudgetCheckResultDisplay();
    }
}

public void run()
{
    super();

    budgetReservation = BudgetReservation_PSN::newFromBudgetReservationHeader_PSN(budgetReservationHeader_PSN);        
}

/// <summary>
/// Sets the form state based on the budget reservation document state and disables the project fields
/// if a purchase requisition with a project is referenced.
/// </summary>    
public void setFormState()
{
    if (budgetReservation.isDocumentCanceled())
    {
        element.design().viewEditMode(ViewEditMode::View);
    }
    else
    {
        element.design().viewEditMode(ViewEditMode::Auto);
    }

    this.enableHeaderButtons();
    this.enableHeaderLineFields();
    this.enableLineButtons();

    budgetReservation.disableProjFieldsIfPurchReqReferenced(budgetReservationLine_PSN_ds);
}

public int active()
{
    int active;

    active = super();        
    element.setFormState();

    return active;
}

