/// <summary>
/// Enables or disables the project related fields.
/// </summary>        
public void updateDesignForProject()
{
    if (budgetReservationLine_PSN.ProjId == '' || budgetReservationLine_PSN.isProjectItemBased())
    {
        budgetReservationLine_PSN_DS.object(fieldNum(BudgetReservationLine_PSN, ProjWorker)).allowEdit(false);
    }
    else
    {
        budgetReservationLine_PSN_DS.object(fieldNum(BudgetReservationLine_PSN, ProjWorker)).allowEdit(true);
    }
}

public int active()
{
