/// <summary>
/// Launches the date picker dialog when the budget parameter is on for using session date for accounting date.
/// </summary>
public void clicked()
{
    if (BudgetParameters::find().UseSessionDateForAccounting == NoYes::Yes)
    {
        Args args = new Args();
        args.record(budgetReservationHeader_PSN);
        args.caller(element);
        args.parmObject(BudgetReservationHeader_PSN_ds);

        MenuFunction menuFunction = new MenuFunction(menuitemActionStr(BudgetReservationCorrectDateDialog_PSN), MenuItemType::Action);

        if (menuFunction && menuFunction.checkAccessRights())
        {
            menuFunction.run(args);
        }
    }
    else
    {
        super();
    }
}

