/// <summary>
/// Iterates marked GBR line, and throws an error message if there is a posted one.
/// </summary>
public void clicked()
{
    MultiSelectionHelper multiSelect = MultiSelectionHelper::construct();

    multiSelect.parmDatasource(budgetReservationLine_PSN_ds);

    BudgetReservationLine_PSN budgetReservationLineMarked = multiSelect.getFirst();

    while(budgetReservationLineMarked)
    {
        if (budgetReservationLineMarked.isPosted())
        {
            throw error(strFmt("@Budgeting_BudgetReservationPS:PostedGBRLineSelected", budgetReservationLineMarked.LineNumber));
        }

        budgetReservationLineMarked = multiSelect.getNext();
    }

    super();
}

public void clicked()
{
    if (!budgetReservationLine_PSN.RecId)
    {
        throw error(Error::missingRecord(tablePName(BudgetReservationLine_PSN)));
    }

    super();
}

