    ret = super(forceUpdate);

    return ret;
}

public void init()
{
    super();

    QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(CFMJournalPaymAccountList));
    QueryBuildRange qbr = qbds.addRange(fieldNum(CFMJournalPaymAccountList, AccountNum));
    qbr.value(SysQuery::valueNotEmptyString());
    qbr.status(RangeStatus::Hidden);
}

