Method.Source:
[Form]
public class CFMPaymentRequest extends FormRun
{
    CFMPaymentRequestFormView formView;

}

public void init()
{
    super();

    formView = CFMPaymentRequestFormView::newFormRun(this, CFMPaymentRequestViewModel::newFromPaymentRequestDataSource(cfmPaymentRequestTable_DS, cfmPaymentRequestLine_DS, cfmPaymentRequestPaymentOrder_DS));
    this.setFormControlsForView();
    formView.bindForm();
}

public void create(boolean _append = false)
{
    boolean newRequestIsCreated = formView.formMethodDataSourceCreatePre();

    formView.formMethodDatasourceCreated();

    if (!newRequestIsCreated)
    {
        if (CFMPaymentRequestView::isCalledFromForm(element.args()))
        {
            element.close();
        }
    }
}

public int active()
{
    int ret = super();

    element.viewModel().paymentRequestActive();

    return ret;
}

public int active()
{
    int ret;

    Dimension_LedgerDimension.clearReference();

    ret = super();

    return ret;
}

public int active()
{
    int ret = super();

    element.viewModel().paymentRequestActive();

    return ret;
}

public RecId getAccountStructure()
{
    RecId ret = super();

    if (cfmPaymentRequestLine.RecId
     && cfmPaymentRequestLine.LedgerDimension)
    {
        ret = DimensionAttributeValueCombination::find(cfmPaymentRequestLine.LedgerDimension).AccountStructure;
    }

    return ret;
}
