Method.Source:
using Microsoft.Dynamics.AX.Framework.Utilities.UrlHelper;

[Form]
public class CLMIntegrationContractTable extends FormRun
{
}

/// <summary>
/// Initializes contract form.
/// </summary>
public void init()
{
    Args args = element.args();

    if (args && args.menuItemName() == menuItemDisplayStr(CLMIntegrationContractTable))
    {
        CLMIntegrationContractTable contract;
        str query = getClientURLQueryValue('q');

        if (query)
        {
            contract = this.getContractTableFromURLQuery(query);

            if (contract.RecId)
            {
                element.changeOpenCompany(CompanyInfo::find(false, contract.LegalEntity).DataArea);
                element.args().lookupRecord(contract);
            }
        }
        else if (args.record() && args.dataset() == tableNum(CLMIntegrationContractTable))
        {
            contract = args.record();
            element.changeOpenCompany(CompanyInfo::find(false, contract.LegalEntity).DataArea);
        }
    }

    super();
}

private CLMIntegrationContractTable getContractTableFromURLQuery(str _queryValue)
{
    CLMIntegrationContractTable contract;

    if (!_queryValue)
    {
        return contract;
    }

    UrlGenerator generator = new UrlGenerator();
    generator.ParseRequestQueryParameter(_queryValue);

    RequestQueryParameterCollection parameterCollection = generator.RequestQueryParameterCollection;

    while (parameterCollection.MoveNext())
    {
        RequestQueryParameter parameter = parameterCollection.Current();

        if (parameter.DataSource == tableStr(CLMIntegrationContractTable))
        {
            contract = this.findRecordFromRequestQueryParameter(parameter);

            if (contract)
            {
                break;
            }
        }
    }

    return contract;
}

private CLMIntegrationContractTable findRecordFromRequestQueryParameter(RequestQueryParameter _requestQueryParameter)
{
    CLMIntegrationContractTable contract;

    for (int i = 0; i < _requestQueryParameter.GetFieldValueCount(); i++)
    {
        var fieldName = _requestQueryParameter.GetField(i);
        var fieldValue = _requestQueryParameter.GetValue(i);

        if (fieldValue && fieldName == fieldStr(CLMIntegrationContractTable, RecId))
        {
            contract = CLMIntegrationContractTable::findByRecId(any2Int64(fieldValue));
            break;
        }
    }

    return contract;
}

private void changeOpenCompany(DataAreaId _companyDataAreaId)
{
    if (_companyDataAreaId != curExt())
    {
        appl.setDefaultCompany(_companyDataAreaId, false);
    }
}

private void updateButtonsEnabled()
{
    CLMIntegrationContractTable contractTable = CLMIntegrationContractTable_DS.cursor();

    PurchaseAgreementButton.enabled(
        CLMIntegrationContractDocumentRelationship::documentRelationshipExist(contractTable.RecId, tableNum(PurchAgreementHeader)));
}

public int task(int _task)
{
    #Task

    if (_task == #taskRefresh)
    {
        CLMIntegrationContractTable currentRecord = CLMIntegrationContractTable_ds.cursor().data() as CLMIntegrationContractTable;
        if (currentRecord)
        {
            element.args().lookupRecord(currentRecord);
        }
    }

    return super(_task);
}

/// <summary>
/// Initializing the Contract Table form data source.
/// </summary>
public void init()
{
    super();

    QueryBuildDataSource qbds = this.queryBuildDataSource();

    QueryBuildRange qbr = qbds.addRange(fieldNum(CLMIntegrationContractTable, LegalEntity));
    qbr.value(queryValue(CompanyInfo::current()));
    qbr.status(RangeStatus::Hidden);
}

/// <summary>
/// Initializing the Contract Account Number form data source.
/// </summary>
public void init()
{
    super();

    QueryBuildDataSource qbds = this.queryBuildDataSource();

    qbds.relations(false);
    qbds.addLink(fieldNum(CLMIntegrationContractTable, RecId), fieldNum(CLMIntegrationContractAccountNumberView, RecId), CLMIntegrationContractTable_DS.name());
    qbds.joinMode(JoinMode::OuterJoin);
}

public void jumpRef()
{
    Args args = new Args();
    MenuFunction menuFunction;

    switch (CLMIntegrationContractTable.ContractAccountType)
    {
        case CLMIntegrationContractAccountType::Cust:
            args.record(CustTable::find(CLMIntegrationContractAccountNumberView.AccountNumber));
            menuFunction = new MenuFunction(menuitemDisplayStr(CustTable), MenuItemType::Display);
            break;

        case CLMIntegrationContractAccountType::Vend:
            args.record(VendTable::find(CLMIntegrationContractAccountNumberView.AccountNumber));
            menuFunction = new MenuFunction(menuitemDisplayStr(VendTable), MenuItemType::Display);
            break;

        case CLMIntegrationContractAccountType::Party:
            args.record(DirPartyTable::find(CLMIntegrationContractAccountNumberView.AccountNumber));
            menuFunction = new MenuFunction(menuitemDisplayStr(DirPartyTable), MenuItemType::Display);
            break;

    }

    menuFunction.run(args);
}

public void clicked()
{
    Args args = new Args();

    int num = CLMIntegrationContractDocumentRelationship::numOfRecordsByContract(CLMIntegrationContractTable.RecId, tableNum(PurchAgreementHeader));
    if (num == 1)
    {
        CLMIntegrationContractDocumentRelationship relationDocument =
            CLMIntegrationContractDocumentRelationship::findByContract(CLMIntegrationContractTable.RecId, tableNum(PurchAgreementHeader));

        PurchAgreementHeader agreementHeader = PurchAgreementHeader::find(relationDocument.DocumentRefRecId);
        args.record(agreementHeader);

        new MenuFunction(menuitemDisplayStr(PurchAgreementDetails), MenuItemType::Display).run(args);
    }
    else if (num > 1)
    {
        args.record(CLMIntegrationContractTable);
        new MenuFunction(menuitemDisplayStr(PurchAgreementListPage), MenuItemType::Display).run(args);
    }
}
