[Form]
public class CatVendorCatalogReviewTool extends FormRun
{
    CompanyInfo selectedLegalEntity;

}

public boolean canSubmitToWorkflow()
{
    // This is a line item workflow. For line item workflows user need not to submit the lines explicitly.
    // The submission will be taken care by header workflow.
    // Returning false will hide the worklflow submit button.
    return false;
}

/// <summary>
/// Enables workflow action bar menu button for bulk approval.
/// </summary>
/// <remarks>
/// By default workflow bar buttons are disabled for multi select.
/// This function enables workflow action bar menu button for multi select.
/// </remarks>
public void enableWorkflowButtonsForBulkApproval()
{
    #Workflow

    FormMenuButtonControl       workflowActionBarMenuButton;

    workflowActionBarMenuButton     = element.design().controlName(#WorkflowActionBarMenuButton);

    if (workflowActionBarMenuButton)
    {
        workflowActionBarMenuButton.multiSelect(true);
    }
}

public void refreshProductPriceDataSource()
{
    dataAreaId legalEntity;
    legalEntity = selectedLegalEntity.DataArea ? selectedLegalEntity.DataArea : curext();
    changecompany(legalEntity)
    {
        catVendorProductCandidatePriceCurrent.setTmpData
                (CatVendorProductCandidatePrice::getProductCandidatePriceCurrentValues
                                                        (catVendorProductCandidate.CatalogMaintenanceRequest));
        if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
        {
            retailVendorProductCandSalesPrice.setTmpData
                (retailVendorProductCandSalesPrice::getProductCandidatePriceCurrentValues
                                                        (catVendorProductCandidate.CatalogMaintenanceRequest));
        }
    }
    catVendorProductCandidatePriceCurrent_ds.research();
}

public void refreshProductDefaultOrderSettingsDataSource()
{
    dataAreaId legalEntity = selectedLegalEntity.DataArea ? selectedLegalEntity.DataArea : curext();
    changecompany(legalEntity)
    {
        CatVendorProductCandidateDefaultOrderSettings.setTmpData
                (CatVendorProductCandidateDefaultOrderSettings::getProductCandidateDefaultOrderSettingsCurrentValues
                                                        (catVendorProductCandidate.CatalogMaintenanceRequest));
    }

    CatVendorProductCandidateDefaultOrderSettings_ds.research();
}

public void executeQuery()
{
    this.query().dataSourceTable(tablenum(CatVendorProductCandidate)).clearRange(fieldnum(CatVendorProductCandidate, ActionType));

    if (actionType.selection() != CatProductActionType::AllItems)
    {
        this.query().dataSourceTable(tablenum(CatVendorProductCandidate)).addRange(fieldnum(CatVendorProductCandidate, ActionType)).value(queryValue(actionType.valueStr()));
    }

    super();
    catVendorTmpProductAttribute.setTmpData(
                            CatVendorTmpProductAttribute::getProductAttributeValues
                            (catVendorProductCandidate.CatalogMaintenanceRequest)
                            );

    catVendorProductCandidatePriceCurrent.setTmp();
    catVendorProductCandidateDefaultOrderSettings.setTmp();

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        retailVendorProductCandSalesPrice.recordLevelSecurity(true);
        retailVendorProductCandSalesPrice.setTmp();
    }

    element.refreshProductPriceDataSource();
    element.refreshProductDefaultOrderSettingsDataSource();
}

public int active()
{
    int ret = super();

    element.enableWorkflowButtonsForBulkApproval();

    return ret;
}

public void init()
{
    QueryBuildDataSource    qbdsProductTextTranslation;
    super();
    qbdsProductTextTranslation = this.query().dataSourceName(catVendorProductNameDesc_ds.name());
    if (qbdsProductTextTranslation)
    {
        qbdsProductTextTranslation.addRange(fieldnum(CatVendorProductTextTranslation,Language)).value(
            queryValue(SystemParameters::getSystemLanguageId()));
    }
}

public int selectionChange()
{
    int ret;
    catVendorProductCandidate_ds.executeQuery();

    ret = super();
    element.updateWorkflowControls();
    return ret;
}

public Common lookupReference()
{
    SysReferenceTableLookup sysTableLookup;
    Query                   query = new Query();
    QueryBuildDataSource    qbdsDirParty, qbdsCompanyInfo,qbdsReleaseCatalog;
    QueryBuildRange         releaseCatalogRange;
    qbdsDirParty    = query.addDataSource(tablenum(DirPartyTable));
    qbdsCompanyInfo = qbdsDirParty.addDataSource(tablenum(CompanyInfo));
    qbdsCompanyInfo.relations(false);
    qbdsCompanyInfo.addLink(fieldnum(CompanyInfo, RecId), fieldnum(DirPartyTable, RecId));
    qbdsCompanyInfo.joinMode(JoinMode::InnerJoin);

    qbdsReleaseCatalog = qbdsCompanyInfo.addDataSource(tableNum(CatVendorReleaseCatalog));
    qbdsReleaseCatalog.relations(true);
    releaseCatalogRange = SysQuery::findOrCreateRange(qbdsReleaseCatalog,fieldNum(CatVendorReleaseCatalog,Catalog));
    releaseCatalogRange.value(queryValue(CatVendorCatalog_LE.RecId));

    sysTableLookup  = SysReferenceTableLookup::newParameters(tablenum(DirPartyTable), this);
    sysTableLookup.addLookupfield(fieldnum(DirPartyTable, Name));
    sysTableLookup.addLookupMethod(tablemethodstr(DirPartyTable, getDataAreaId));
    sysTableLookup.parmQuery(query);

    return sysTableLookup.performFormLookup();
}

public boolean modified()
{
    boolean         ret;

    ret = super();

    selectedLegalEntity = CompanyInfo::findRecId(legalEntityReference.value());

    element.refreshProductPriceDataSource();
    element.refreshProductDefaultOrderSettingsDataSource();

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        retailVendorProductCandSalesPrice_DS.research();
    }
    return ret;
}

public Common resolveReference()
{
    Common          selectedRecord;
    DirPartyTable   dirPartyTable;
    CompanyInfo     companyInfo;
    DirPartyName    partyName;

    partyName = this.filterValue(AbsoluteFieldBinding::construct(fieldstr(DirPartyTable, Name), tablestr(DirPartyTable))).value();

    select count(RecId) from dirPartyTable
            join companyInfo
                where companyInfo.RecId == dirPartyTable.RecId
                    && dirPartyTable.Name == partyName;

    if (dirPartyTable.RecId == 0)
    {
        // Assigning null to selectedRecord does not cause the kernel to receive a true NULL value, but rather
        // just an empty record. Return NULL directly so the kernel knows that the intent is to not clear the
        // selected value.
        return null;
    }
    else if (dirPartyTable.RecId > 1)
    {
        selectedRecord = this.lookupReference();
    }
    else
    {
        select firstonly * from dirPartyTable
                join * from companyInfo
                    where companyInfo.RecId == dirPartyTable.RecId
                        && dirPartyTable.Name == partyName;

        selectedRecord = dirPartyTable;
    }

    return selectedRecord.RecId? selectedRecord : null;
}
