NumberSeqFormHandler numberSeqFormHandler()
{
    if (!numberSeqFormHandler)
    {
        numberSeqFormHandler = NumberSeqFormHandler::newForm(NumberSeqReference::findReference(extendedTypeNum(CommReportId_RU)).NumberSequenceId,
                                                         element,
                                                         commReportJour_DS,
                                                         fieldnum(CommReportJour_RU, ReportId)
                                                        );
    }
    return numberSeqFormHandler;
}

public void run()
