    super();

    this.updateDesign();
}

void updateDataSource()
{
    FactureJour_RU       factureJour;
    QueryBuildDataSource qbds;
    TableId              tableId;
    FieldId              fieldId;
    FormDataSource       ds;

    if (element.args().dataset() == tableNum(FactureJour_RU))
    {
        factureJour = element.args().record();

        if (factureJour.FactureType == FactureType_RU::Prepayment)
        {
            tableId = tableNum(CommReportTransPrepayment_RU);
            fieldId = fieldNum(CommReportTransPrepayment_RU, RefFactureJour);
            ds      = commReportTransPrepay_ds;

            tabLine.tab(3);
            tabLine.tabChanged(1, 3);
        }
        else
        {
            tableId = tableNum(CommReportTransItem_RU);
            fieldId = fieldNum(CommReportTransItem_RU, RefFactureJour);
            ds      = commReportTransItem_ds;
        }

        qbds = commReportJour_ds.query().dataSourceTable(tableNum(CommReportJour_RU));
        qbds = qbds.addDataSource(tableId);
        qbds.relations(true);
        qbds.joinMode(JoinMode::ExistsJoin);

        findOrCreateRange_W(qbds, fieldId, queryValue(factureJour.RecId));

        qbds = ds.query().dataSourceTable(tableId);

        findOrCreateRange_W(qbds, fieldId, queryValue(factureJour.RecId));
    }

    qbds = commReportTransItem_ds.query().dataSourceTable(tableNum(CustInvoiceTrans));
    qbds.clearLinks();
    qbds.addLink(fieldNum(CommReportTransItem_RU, RefInvoiceTrans), fieldNum(CustInvoiceTrans, RecId));
}

void updateDesign()
{
    boolean isPurchaseAgreement = commReportJour.PartnerType == ModuleCustVend::Cust;

    vendInvoiceAccount.visible(isPurchaseAgreement);
    vendInvoiceName.visible(isPurchaseAgreement);

    custInvoiceAccount.visible(! isPurchaseAgreement);
    custInvoiceName.visible(! isPurchaseAgreement);

    vendInvoiceAccountGen.visible(isPurchaseAgreement);
    vendInvoiceNameGen.visible(isPurchaseAgreement);

    custInvoiceAccountGen.visible(! isPurchaseAgreement);
    custInvoiceNameGen.visible(! isPurchaseAgreement);

    custInvoiceJournal.visible(! isPurchaseAgreement);
    vendInvoiceJournal.visible(isPurchaseAgreement);

    if (element.args().dataset() == tableNum(FactureJour_RU))
    {
        commReportJour_ds.allowCreate(false);
        commReportJour_ds.allowEdit(false);
        commReportJour_ds.allowDelete(false);
        buttonGroupHeader.visible(false);
    }
    else
    {
        commReportJour_ds.allowDelete(! commReportJour.existsApproved());
    }

    commReportTransItem_ds.allowDelete(commReportTransItem.Approved != NoYes::Yes);

    if (tabLine.tab() != 3)
    {
        factureEditLinesCust.visible(isPurchaseAgreement && commReportTransItem.RecId);
        factureEditLinesVend.visible(! isPurchaseAgreement && commReportTransItem.RecId);

        factureCust.visible(isPurchaseAgreement);
        factureVend.visible(! isPurchaseAgreement);

        custInvoiceJournalPartner.visible(isPurchaseAgreement);
        vendInvoiceJournalPartner.visible(! isPurchaseAgreement);
    }
    else
    {
        custCreatePrepaymentFacture.visible(isPurchaseAgreement);
        vendCreatePrepaymentFacture.visible(! isPurchaseAgreement);

        vendAccountPrepay.visible(isPurchaseAgreement);
        custAccountPrepay.visible(! isPurchaseAgreement);

        vendNamePrepay.visible(isPurchaseAgreement);
        custNamePrepay.visible(! isPurchaseAgreement);

        vendTransPrepay.visible(isPurchaseAgreement);
        custTransPrepay.visible(! isPurchaseAgreement);
    }
}

