public void create(boolean _append = false)
{
    FormRun formRun;
    Args   args = new Args(formStr(CommReportCreate_RU));

    formRun = classFactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait();

    if (formRun.closedOk())
    {
        element.numberSeqFormHandler().formMethodDataSourceCreatePre();

        super(_append);

        buf2Buf(formRun.getJourHeader(), commReportJour);

        element.updateDesign();

        element.numberSeqFormHandler().formMethodDataSourceCreate(true);
    }
}

public void jumpRef()
{
    AgreementHeader agreementHeader = AgreementHeader::find(commReportJour.agreementHeaderExt().AgreementHeader);
    Args            args = new Args();

    args.record(agreementHeader);

    switch (commReportJour.PartnerType)
    {
        case ModuleCustVend::Cust :
            new MenuFunction(menuitemDisplayStr(SalesAgreement), MenuItemType::Display).run(args);
            break;
        case ModuleCustVend::Vend :
            new MenuFunction(menuitemDisplayStr(PurchAgreement), MenuItemType::Display).run(args);
            break;
    }
}

