public void modified()
{
    CustVendTrans prepaymTrans = commReportTransPrepay.custVendTrans();
    AmountMST     maxAmount    = abs(prepaymTrans.AmountMST) - prepaymTrans.custVendTrans::getCommPrepaymentUsedAmount_RU(commReportTransPrepay.RecId);

    super();

    if (commReportTransPrepay.LineAmountMST < 0 ||
        commReportTransPrepay.LineAmountMST > maxAmount)
    {
        commReportTransPrepay.LineAmountMST = maxAmount;
    }

    commReportTransPrepay.calcTax();
    commReportTransPrepay_ds.write();
}

public void lookup(FormControl _formControl, str _filterStr)
{
    TableLookup_RU          sysTableLookup;
    Query                   query = new Query();
    CustVendTrans           custVendTrans;
    QueryBuildDataSource    qbds;

    if (commReportJour.PartnerType == ModuleCustVend::Cust)
    {
        sysTableLookup = TableLookup_RU::newParameters(tableNum(VendTrans), _formControl);
        sysTableLookup.addLookupfield(fieldNum(VendTrans, Voucher), true);
        sysTableLookup.addLookupfield(fieldNum(VendTrans, TransDate));
        sysTableLookup.addLookupfield(fieldNum(VendTrans, AccountNum));
        sysTableLookup.addLookupfield(fieldNum(VendTrans, AmountMST));
        sysTableLookup.addLookupfield(fieldNum(VendTrans, DocumentNum));
        sysTableLookup.addLookupfield(fieldNum(VendTrans, DocumentDate));

        qbds = query.addDataSource(tableNum(VendTrans));
        findOrCreateRange_W(qbds, fieldNum(VendTrans, Prepayment), queryValue(NoYes::Yes));
        findOrCreateRange_W(qbds, fieldNum(VendTrans, TransDate), queryRange(commReportJour.StartDate, commReportJour.EndDate));
        findOrCreateRange_W(qbds, fieldNum(VendTrans, Voucher), _filterStr);

        qbds = qbds.addDataSource(tableNum(VendTrans_W));
        qbds.relations(true);
        qbds.joinMode(JoinMode::ExistsJoin);

        findOrCreateRange_W(qbds, fieldNum(VendTrans_W, CommProfileType_RU), SysQuery::valueNot(CommProfileType_RU::General));
        findOrCreateRange_W(qbds, fieldNum(VendTrans_W, PrepaymentFactureId_W), SysQuery::valueNotEmptyString());

        qbds = query.dataSourceTable(tableNum(VendTrans)).addDataSource(tableNum(CommPrepaymAllocRef_RU));
        qbds.addLink(fieldNum(VendTrans, TableId), fieldNum(CommPrepaymAllocRef_RU, RefTableId));
        qbds.addLink(fieldNum(VendTrans, RecId),   fieldNum(CommPrepaymAllocRef_RU, RefRecId));
        qbds.joinMode(JoinMode::NoExistsJoin);
    }
    else
    {
        sysTableLookup = TableLookup_RU::newParameters(tableNum(CustTrans), _formControl);
        sysTableLookup.addLookupfield(fieldNum(CustTrans, Voucher), true);
        sysTableLookup.addLookupfield(fieldNum(CustTrans, TransDate));
        sysTableLookup.addLookupfield(fieldNum(CustTrans, AccountNum));
        sysTableLookup.addLookupfield(fieldNum(CustTrans, AmountMST));
        sysTableLookup.addLookupfield(fieldNum(CustTrans, DocumentNum));
        sysTableLookup.addLookupfield(fieldNum(CustTrans, DocumentDate));

        qbds = query.addDataSource(tableNum(CustTrans));
        findOrCreateRange_W(qbds, fieldNum(CustTrans, Prepayment), queryValue(NoYes::Yes));
        findOrCreateRange_W(qbds, fieldNum(CustTrans, TransDate), queryRange(commReportJour.StartDate, commReportJour.EndDate));
        findOrCreateRange_W(qbds, fieldNum(CustTrans, Voucher), _filterStr);

        qbds = qbds.addDataSource(tableNum(CustTrans_W));
        qbds.relations(true);
        qbds.joinMode(JoinMode::ExistsJoin);

        findOrCreateRange_W(qbds, fieldNum(CustTrans_W, CommProfileType_RU), SysQuery::valueNot(CommProfileType_RU::General));
        findOrCreateRange_W(qbds, fieldNum(CustTrans_W, PrepaymentFactureId_W), SysQuery::valueNotEmptyString());

        qbds = query.dataSourceTable(tableNum(CustTrans)).addDataSource(tableNum(CommPrepaymAllocRef_RU));
        qbds.addLink(fieldNum(CustTrans, TableId), fieldNum(CommPrepaymAllocRef_RU, RefTableId));
        qbds.addLink(fieldNum(CustTrans, RecId),   fieldNum(CommPrepaymAllocRef_RU, RefRecId));
        qbds.joinMode(JoinMode::NoExistsJoin);
    }

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
    if (sysTableLookup.getFoundCursor())
    {
        custVendTrans = sysTableLookup.getFoundCursor();

        commReportTransPrepay.initFromReference(custVendTrans.TableId, custVendTrans.RecId);
        commReportTransPrepay_ds.write();
    }
}

