public void tabChanged(int _fromTab, int _toTab)
{
    super(_FromTab, _ToTab);

    buttonGroupItem.visible(_toTab != 3);
    deleteGroupItem.visible(_toTab != 3);
    newDeleteGroupPrep.visible(_toTab == 3);
    buttonGroupPrep.visible(_toTab == 3);

    element.updateDesign();
}
