public void init()
{
    if (element.args().caller())
    {
        sourceVendInvoiceJour.data(element.args().record());
    }

    costAllocationForm = CostAllocationForm_RU::construct();
    costAllocationForm.initParams(element);
    costAllocationForm.sourceVendInvoiceJour(sourceVendInvoiceJour);

    super();

    costAllocationForm.parmAllocationType(allocationType);

    costAllocationForm.initDatasource(costAllocationBase_ds,
                                      costAllocationResult_ds,
                                      vendInvoiceTrans_ds);

    costAllocationForm.initAmountsCtrl(amountNotAlloc,
                                       amount4Alloc,
                                       amountAlloc,
                                       amountAllocResult);

    costAllocationForm.initButtons(buttonCostAllocationPost,
                                   buttonCostAllocationMakeResult,
                                   costAllocationSelectBase);

    costAllocationForm.initControls(showCancelled,
                                    allocateAfter,
                                    postingDate,
                                    amountGroup,
                                    editAmount4Alloc,
                                    gridResult,
                                    gridAllocBaseIndebtedness,
                                    allocBaseIndebtedness,
                                    gridAllocBaseOverhead,
                                    allocBaseOverhead,
                                    gridAllocBaseExpenses,
                                    allocBaseExpenses,
                                    allocBaseDimension,
                                    allocResult,
                                    dateIntervalGroup,
                                    fromDate,
                                    toDate,
                                    cancelGroup,
                                    stockedProduct,
                                    baseNewDeleteGroup);

    costAllocationForm.initSelectAllBaseButton(SelectAllBase);

    DimensionEntryControl.allowEdit(costAllocationForm.costAllocationProcessType() == CostAllocationProcessType_RU::Allocation && costAllocationForm.allocationType() != CostAllocationType_RU::Overhead);
}

