public int task(int _taskId)
{
    #Task
    int ret;

    if (_taskId == #taskDeleteRecord)
    {
        if (Box::yesNo("@SYS100114", DialogButton::No) == DialogButton::Yes)
        {
            costAllocationBase_DS.deleteMarkedRecords();
            costAllocationForm.executeDS();
        }
        // Don't call super() for the delete task
    }
    else
    {
        // Handle every other task as normal
        ret = super(_taskId);
    }

    return ret;
}

public int active()
{
    int ret = super();

    if (ret)
    {
        if (costAllocationForm.costAllocationProcessType() == CostAllocationProcessType_RU::Cancel)
        {
            costAllocationForm.getTablesFromHistory(true);
        }
        else
        {
            costAllocationForm.initCategoryBaseDesign();
        }
    }

    return ret;
}

public void executeQuery()
{
    if (qbrInvoiceDate)
    {
        qbrInvoiceDate.value(SysQuery::range((fromDate.dateValue() ? fromDate.dateValue() : dateNull()),
                                             (toDate.dateValue() ? toDate.dateValue() : dateNull())));
    }

    super();
}

public void init()
{
    QueryBuildDataSource    qbdsVendInvoiceTrans;
    QueryBuildDataSource    qbdsCostAllocationHistory;

    super();

    qbdsVendInvoiceTrans = this.query().dataSourceTable(tablenum(VendInvoiceTrans));
    qbdsVendInvoiceTrans.clearDynalinks();

    if (sourceVendInvoiceJour.RecId)
    {
        qbdsVendInvoiceTrans.addRange(fieldnum(VendInvoiceTrans, PurchID)).value(sourceVendInvoiceJour.PurchId);
        qbdsVendInvoiceTrans.addRange(fieldnum(VendInvoiceTrans, InvoiceId)).value(sourceVendInvoiceJour.InvoiceId);
        qbdsVendInvoiceTrans.addRange(fieldnum(VendInvoiceTrans, NumberSequenceGroup)).value(sourceVendInvoiceJour.NumberSequenceGroup);
        qbdsVendInvoiceTrans.addRange(fieldnum(VendInvoiceTrans, InternalInvoiceId)).value(sourceVendInvoiceJour.InternalInvoiceId);
        qbdsVendInvoiceTrans.addRange(fieldnum(VendInvoiceTrans, InvoiceDate)).value(queryValue(sourceVendInvoiceJour.InvoiceDate));
    }
    else if (costAllocationForm.costAllocationProcessType() == CostAllocationProcessType_RU::Cancel)
    {
        qbrInvoiceDate = this.query().dataSourceTable(tablenum(VendInvoiceTrans)).addRange(fieldnum(VendInvoiceTrans, InvoiceDate));
    }

    SysQuery::addLockRange_RU(this.query().dataSourceTable(tablenum(VendInvoiceTrans)),
                              fieldnum(VendInvoiceTrans, LineAmount),
                              queryValue(">0"));

    SysQuery::addLockRange_RU(this.query().dataSourceTable(tablenum(VendInvoiceTrans)),
                              fieldnum(VendInvoiceTrans, MarkupCode_RU),
                              SysQuery::valueNotEmptyString());

    if (costAllocationForm.costAllocationProcessType() == CostAllocationProcessType_RU::Cancel)
    {
        qbdsCostAllocationHistory = this.query().dataSourceTable(tablenum(VendInvoiceTrans)).addDataSource(tablenum(CostAllocationHistory_RU));
        qbdsCostAllocationHistory.addLink(fieldnum(VendInvoiceTrans,RecId), fieldnum(CostAllocationHistory_RU, AllocatedInvoiceTransRecId));
        qbdsCostAllocationHistory.joinMode(JoinMode::ExistsJoin);
    }
}

public edit AmountMST editAmount4Alloc(boolean             _set,
                                       VendInvoiceTrans    _vendInvoiceTrans,
                                       AmountMST           _amount4Alloc)
{
    MarkupAmountNotAlloc_RU markupAmountNotAlloc;

    if (_set)
    {
        if (_amount4Alloc < 0)
        {
            _amount4Alloc = 0;
        }
        else
        {
            markupAmountNotAlloc = _vendInvoiceTrans.markupAmountNotAlloc_RU();
            if (_amount4Alloc > markupAmountNotAlloc)
            {
                _amount4Alloc = markupAmountNotAlloc;
            }
        }

        costAllocationForm.editMarkRecord(_set, _vendInvoiceTrans, _amount4Alloc != 0);
    }

    return costAllocationForm.editAmount4Alloc(_set, _vendInvoiceTrans, _amount4Alloc);
}

public edit NoYes invoiceMarkRecord(boolean            _set,
                                    VendInvoiceTrans   _vendInvoiceTrans,
                                    NoYes              _mark)
{
    if (_set)
    {
        if (costAllocationForm.costAllocationProcessType() == CostAllocationProcessType_RU::Allocation)
        {
            if (_mark)
            {
                vendInvoiceTrans_ds.editAmount4Alloc(true, _vendInvoiceTrans, _vendInvoiceTrans.markupAmountNotAlloc_RU());
            }
            else
            {
                vendInvoiceTrans_ds.editAmount4Alloc(true, _vendInvoiceTrans, 0);
            }
        }

        costAllocationForm.markResultRecordFromVendInvoice(_vendInvoiceTrans.RecId, _mark);
    }

    return costAllocationForm.editMarkRecord(_set, _vendInvoiceTrans, _mark);
}

