public void executeQuery()
{
    while (this.query().dataSourceNo(1).findRange(fieldnum(TmpCostAllocationResult_RU, Cancelled)))
    {
        this.query().dataSourceNo(1).clearRange(fieldnum(TmpCostAllocationResult_RU, Cancelled));
    }

    if (showCancelled.value() == NoYes::No)
    {
        this.query().dataSourceNo(1).addRange(fieldnum(TmpCostAllocationResult_RU, Cancelled)).value(queryValue(NoYes::No));
    }

    super();
}

public edit AmountMST editAmountAllocResult(boolean _set, TmpCostAllocationResult_RU _costAllocationResult, Amount _amountAlloc)
{
    if (_set)
    {
        if (_amountAlloc < 0 ||
            (costAllocationResult.Mark && !costAllocationForm.checkChangeResult(costAllocationResult, _amountAlloc)))
        {
            _amountAlloc = _costAllocationResult.Amount;
        }

        _costAllocationResult.Amount = _amountAlloc;
        gridSource.update();
    }

    return _costAllocationResult.Amount;
}

