public void jumpRef()
{
    FormRun         formRun;

    if (costAllocationBase.CustVend == CustVend_RU::Cust)
    {
        formRun = new MenuFunction(menuItemDisplayStr(CustInvoiceJournal), MenuItemType::Display).create(new Args());
        formRun.run();
        formRun.dataSource().findRecord(CustInvoiceJour::findRecId(costAllocationResult.IndebtednessInvoiceJourRecId));
        formRun.detach();
    }
    else
    if (costAllocationBase.CustVend == CustVend_RU::Vend)
    {
        formRun = new MenuFunction(menuItemDisplayStr(VendInvoiceJournal), MenuItemType::Display).create(new Args());
        formRun.run();
        formRun.dataSource().findRecord(VendInvoiceJour::findRecId(costAllocationResult.IndebtednessInvoiceJourRecId));
        formRun.detach();
    }
}

public void modified()
{
    super();

    costAllocationForm.markResultRecord(costAllocationResult, costAllocationResult.Mark);

    if (costAllocationForm.costAllocationProcessType() == CostAllocationProcessType_RU::Cancel)
    {
        costAllocationResult_DS.executeQuery();
    }
}

public void deleteMarkedRecords()
{
    TmpCostAllocationBase_RU    deleteCostAllocationBase;
    TmpCostAllocationBase_RU    locCostAllocationBase;

    void deleteRecord(TmpCostAllocationBase_RU _costAllocationBase)
    {
        if (_costAllocationBase.RecId)
        {
            this.baseMarkRecord(NoYes::Yes, _costAllocationBase, NoYes::No);

            delete_from costAllocationResult
                where costAllocationResult.AllocBaseRecId == _costAllocationBase.RecId;

            _costAllocationBase.delete();
        }
    }

    deleteCostAllocationBase = this.getFirst(1);
    if (deleteCostAllocationBase.RecId == 0)
    {
        deleteRecord(costAllocationBase);
    }
    else
    {
        locCostAllocationBase.setTmpData(costAllocationBase);
        while (deleteCostAllocationBase.RecId)
        {
            select firstonly forupdate locCostAllocationBase
                where locCostAllocationBase.RecId == deleteCostAllocationBase.RecId;

            deleteRecord(locCostAllocationBase);
            deleteCostAllocationBase = this.getNext();
        }
    }

    if (costAllocationForm.costAllocationProcessType() == CostAllocationProcessType_RU::Allocation)
    {
        costAllocationForm.runCostAllocationMakeResult(true);
    }
}

public boolean validateWrite()
{
    boolean ret = super();

    if (ret)
    {
        if (allocationType.selection() == CostAllocationType_RU::Indebtedness)
        {
            if (!costAllocationBase.CustVend)
            {
                ret = checkFailed(strfmt("@SYS26332", fieldid2pname(tablenum(TmpCostAllocationBase_RU), fieldnum(TmpCostAllocationBase_RU, CustVend))));
            }

            if (!costAllocationBase.AccountNum)
            {
                ret = checkFailed(strfmt("@SYS26332", fieldid2pname(tablenum(TmpCostAllocationBase_RU), fieldnum(TmpCostAllocationBase_RU, AccountNum))));
            }
        }
    }

    return ret;
}

public edit NoYes baseMarkRecord(boolean _set, TmpCostAllocationBase_RU  _costAllocationBase, NoYes _mark)
{
    if (_set)
    {
        costAllocationForm.markResultRecordFromAllocationBase(_costAllocationBase.RecId, _mark);
    }

    return costAllocationForm.editMarkRecord(_set, _costAllocationBase, _mark);
}

public void lookup(FormControl _formControl, str _filterStr)
{
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;
    Args                    args;
    FormRun                 formRun;
    FormDataSource          formDataSource;
    FormStringControl       formControl = _formControl;

    if (costAllocationBase.CustVend == CustVend_RU::Vend &&
        allocationType.selection() == CostAllocationType_RU::Indebtedness)
    {
        args = new Args();
        args.name(formstr(VendTableLookup));
        args.caller(_formControl);
        args.lookupField(fieldnum(VendTable, AccountNum));
        args.lookupValue(formControl.text());
        formRun = classfactory.formRunClass(args);
        formRun.init();

        formDataSource          = formRun.dataSource();
        queryBuildDataSource    = formDataSource.query().dataSourceTable(tablenum(VendTable));
        queryBuildRange         = queryBuildDataSource.addRange(fieldnum(VendTable, AccountNum));
        queryBuildRange.value(SysQuery::valueNot(sourceVendInvoiceJour.InvoiceAccount));

        formControl.performFormLookup(formRun);
    }
    else
    {
        super(_formControl, _filterStr);
    }
}

public str resolveAmbiguousReference(FormControl _formControl)
{
    if (costAllocationBase.CustVend == CustVend_RU::Vend &&
        allocationType.selection() == CostAllocationType_RU::Indebtedness)
    {
        return VendTable::resolveAmbiguousReferenceVendor(_formControl);
    }

    return super();
}

