void clicked()
{
    boolean                     ret;
    CostAllocationHistory_RU    costAllocationHistory;

    if (vendInvoiceTrans.RecId)
    {
        select firstonly RecId from costAllocationHistory
            where costAllocationHistory.AllocatedInvoiceTransRecId == vendInvoiceTrans.RecId;

        ret = costAllocationHistory.RecId ? true : false;
    }

    ledgerTransactVoucher.enabled(ret);

    super();
}

