/// <summary>
/// The <c>CostCalculationCompareDialog</c> dialog compares cost calculations for a production order.
/// </summary>
[Form]
public class CostCalculationCompareDialog extends FormRun implements IChartClickable
{
    CostCalculationCompareChartViewModel    totalCostChartViewModel;
    CostCalculationCompareChartViewModel    unitCostChartViewModel;
    ProdTable                               prodTable;

}

/// <summary>
/// Initializes the chart.
/// </summary>
public void init()
{
    super();

    prodTable = element.args().record();

    this.initProductNamePicker();
}

private void initProductNamePicker()
{
    boolean isBatchOrder = (prodTable.ProdType == ProdType::Process);

    ProductPickerTabPage.visible(isBatchOrder);

    costCalculationCompareProductPickerTmp.ItemId = prodTable.ItemId;
    costCalculationCompareProductPickerTmp.InventTransId = prodTable.InventTransId;
    costCalculationCompareProductPickerTmp.InventDimId = prodTable.InventDimId;
    costCalculationCompareProductPickerTmp.ProductType = CostCalculationCompareProductType::MainProduct;
    costCalculationCompareProductPickerTmp.insert();

    if (isBatchOrder)
    {
        PmfProdCoBy pmfProdCoBy;

        while select ItemId, InventDimId, InventTransId, ProductType from pmfProdCoBy
            where pmfProdCoBy.ProdId == prodTable.ProdId
        {
            costCalculationCompareProductPickerTmp.ItemId = pmfProdCoBy.ItemId;
            costCalculationCompareProductPickerTmp.InventTransId = pmfProdCoBy.InventTransId;
            costCalculationCompareProductPickerTmp.InventDimId = pmfProdCoBy.InventDimId;
            costCalculationCompareProductPickerTmp.ProductType = this.pmfProductType2CostCalculationCompareProductType(pmfProdCoBy.ProductType);
            costCalculationCompareProductPickerTmp.insert();
        }
    }
}

private void initCharts(CostRollupCompareArgs _costCalculationCompareArgs)
{
    totalCostChartViewModel = CostCalculationCompareChartViewModel::construct();
    totalCostChartViewModel.setChartControl(TotalCostCalculationCompareChart);
    totalCostChartViewModel.setHomeControl(TotalCostHomeButton);
    totalCostChartViewModel.setCostGroupTypeControl(TotalCostCostGroupTypeButton);
    totalCostChartViewModel.setCostGroupControl(TotalCostCostGroupButton);
    totalCostChartViewModel.setDataSource(TotalCostCalculationCompareChartTmp_DS);
    totalCostChartViewModel.isChartUnitCostBased(false);
    totalCostChartViewModel.init(_costCalculationCompareArgs);
    this.onTotalCostChartDrillThroughClicked += eventhandler(totalCostChartViewModel.drillThroughClicked);

    unitCostChartViewModel = CostCalculationCompareChartViewModel::newFromCostCalculationCompareChartViewModel(totalCostChartViewModel);
    unitCostChartViewModel.setChartControl(UnitCostCalculationCompareChart);
    unitCostChartViewModel.setHomeControl(UnitCostHomeButton);
    unitCostChartViewModel.setCostGroupTypeControl(UnitCostCostGroupTypeButton);
    unitCostChartViewModel.setCostGroupControl(UnitCostCostGroupButton);
    unitCostChartViewModel.setDataSource(UnitCostCalculationCompareChartTmp_DS);
    unitCostChartViewModel.isChartUnitCostBased(true);
    unitCostChartViewModel.init(_costCalculationCompareArgs);
    this.onUnitCostChartDrillThroughClicked += eventhandler(unitCostChartViewModel.drillThroughClicked);
}

/// <summary>
/// Event for signaling total cost chart drill through.
/// </summary>
/// <param name = "_args"><c>Args</c> class instantiated by the chart control, containing clicked value information</param>
delegate void onTotalCostChartDrillThroughClicked(Args _args) { }

/// <summary>
/// Event for signaling unit cost chart drill through.
/// </summary>
/// <param name = "_args"><c>Args</c> class instantiated by the chart control, containing clicked value information</param>
delegate void onUnitCostChartDrillThroughClicked(Args _args) { }

public boolean drillThroughClicked(Args _args, MenuFunction _mf)
{
    if (_mf.name() == menuItemActionStr(CostCalculationCompareTotalCostDrilldown))
    {
        this.onTotalCostChartDrillThroughClicked(_args);
    }
    else if (_mf.name() == menuItemActionStr(CostCalculationCompareUnitCostDrilldown))
    {
        this.onUnitCostChartDrillThroughClicked(_args);
    }
    return true;
}

public int active()
{
    int ret = super();

    var costCalculationCompareArgs = CostRollupCompareArgs::newFromProdTable(
                                        prodTable,
                                        costCalculationCompareProductPickerTmp.ItemId,
                                        costCalculationCompareProductPickerTmp.InventTransId,
                                        costCalculationCompareProductPickerTmp.InventDimId);

    element.initCharts(costCalculationCompareArgs);

    return ret;
}
