Query buildQuery()
{
    Query q = new Query();

    qb_tranCommittedCost    = q.addDataSource(tableNum(CostControlTransCommittedCost));
    criteriaEmplItemId      = qb_tranCommittedCost.addRange(fieldNum(CostControlTransCommittedCost, EmplItemId));
    criteriaResourceRecId   = qb_tranCommittedCost.addRange(fieldNum(CostControlTransCommittedCost, Resource));
    criteriaResourceCategoryRecId   = qb_tranCommittedCost.addRange(fieldNum(CostControlTransCommittedCost, ResourceCategory));
    criteriaTransDate       = qb_tranCommittedCost.addRange(fieldNum(CostControlTransCommittedCost, CommittedDate));
    criteriaCategoryId      = qb_tranCommittedCost.addRange(fieldNum(CostControlTransCommittedCost, CategoryId));
    criteriaStatus          = qb_tranCommittedCost.addRange(fieldNum(CostControlTransCommittedCost, Open));
    criteriaTransId         = qb_tranCommittedCost.addRange(fieldNum(CostControlTransCommittedCost, ProjTransId));
    criteriaOrigin          = qb_tranCommittedCost.addRange(fieldNum(CostControlTransCommittedCost, CommittedCostOrig));
    criteriaLineNumber      = qb_tranCommittedCost.addRange(fieldNum(CostControlTransCommittedCost, LineNum));
    criteriaProjTransType   = qb_tranCommittedCost.addRange(fieldNum(CostControlTransCommittedCost, ProjTransType));
    criteriaReferenceId     = qb_tranCommittedCost.addRange(fieldNum(CostControlTransCommittedCost, RefId));
    criteriaSourceDocumentLine = qb_tranCommittedCost.addRange(fieldNum(CostControlTransCommittedCost, SourceDocumentLine));

    if (element.categoryTable())
    {
        qb_projCategoryTable = qb_tranCommittedCost.addDataSource(tableNum(ProjCategory));
        qb_projCategoryTable.addLink(fieldNum(CostControlTransCommittedCost, CategoryId), fieldNum(ProjCategory, CategoryId));
        qb_projCategoryTable.fetchMode(0);
        qb_projCategoryTable.joinMode(JoinMode::ExistsJoin);

        criteriaCategoryGroupId = qb_projCategoryTable.addRange(fieldNum(ProjCategory, CategoryGroupId));
    }

    qb_projTable = qb_tranCommittedCost.addDataSource(tableNum(ProjTable));
    qb_projTable.addLink(fieldNum(CostControlTransCommittedCost, ProjId), fieldNum(ProjTable, ProjId));
    qb_projTable.fetchMode(0);
    qb_projTable.joinMode(JoinMode::ExistsJoin);

    criteriaProjInvoiceProjId   = qb_projTable.addRange(fieldNum(ProjTable, ProjInvoiceProjId));
    criteriaProjId              = qb_projTable.addRange(fieldNum(ProjTable, ProjId));
    criteriaWIPProjId           = qb_projTable.addRange(fieldNum(ProjTable, wipProject));
    if (!enableProjMultipleContractLinesForProjectFeature)
    {
        criteriaFixedPrice          = qb_projTable.addRange(fieldNum(ProjTable, Type));
        criteriaTimeMaterial        = qb_projTable.addRange(fieldNum(ProjTable, Type));
        criteriaInternal            = qb_projTable.addRange(fieldNum(ProjTable, Type));
        criteriaCost                = qb_projTable.addRange(fieldNum(ProjTable, Type));
        criteriaInvestment          = qb_projTable.addRange(fieldNum(ProjTable, Type));
        criteriaTime                = qb_projTable.addRange(fieldNum(ProjTable, Type));
    }

    criteriaSorting             = qb_projTable.addRange(fieldNum(ProjTable, SortingId), 1);
    criteriaSorting2            = qb_projTable.addRange(fieldNum(ProjTable, SortingId), 2);
    criteriaSorting3            = qb_projTable.addRange(fieldNum(ProjTable, SortingId), 3);
    criteriaGroupId             = qb_projTable.addRange(fieldNum(ProjTable, ProjGroupId));
    criteriaCustAccount         = qb_projTable.addRange(fieldNum(ProjTable, CustAccount));

    return q;
}

void linkProjCostControl()
{
    #define.star('*')

    if (projCostControl.parmLedgerTransDateFrom() || projCostControl.parmLedgerTransDateTo())
    {
        criteriaTransDate.value(strFmt('%1..%2',queryValue(projCostControl.parmLedgerTransDateFrom()),queryValue(projCostControl.parmLedgerTransDateTo())));
    }

    if (projCostControl.parmResource())
    {
        criteriaResourceRecId.value(queryValue(projCostControl.parmResource()));
    }

    if (projCostControl.parmResourceCategory())
    {
        criteriaResourceCategoryRecId.value(queryValue(projCostControl.parmResourceCategory()));
    }

    if (projCostControl.parmItemId())
    {
        criteriaEmplItemId.value(projCostControl.parmItemId());
    }

    if (projCostControl.parmProjCategoryId())
    {
        criteriaCategoryId.value(projCostControl.parmProjCategoryId());
    }

    if (projCostControl.parmCategoryGroupId() && criteriaCategoryGroupId)
    {
        criteriaCategoryGroupId.value(projCostControl.parmCategoryGroupId());
    }

    if (!enableProjMultipleContractLinesForProjectFeature)
    {
        if (projCostControl.parmFixedPrice())
        {
            criteriaFixedPrice.value(queryValue(ProjType::FixedPrice));
        }
        else
        {
            criteriaFixedPrice.value(queryValue(SysQuery::valueEmptyString()));
        }

        if (projCostControl.parmTimeMaterial())
        {
            criteriaTimeMaterial.value(queryValue(ProjType::TimeMaterial));
        }
        else
        {
            criteriaTimeMaterial.value(queryValue(SysQuery::valueEmptyString()));
        }

        if (projCostControl.parmInternalCost())
        {
            criteriaInternal.value(queryValue(ProjType::Cost));
        }
        else
        {
            criteriaInternal.value(queryValue(SysQuery::valueEmptyString()));
        }

        if (projCostControl.parmInternalCost())
        {
            criteriaCost.value(queryValue(ProjType::Internal));
        }
        else
        {
            criteriaCost.value(queryValue(SysQuery::valueEmptyString()));
        }

        if (projCostControl.parmInvestment())
        {
            criteriaInvestment.value(queryValue(ProjType::Investment));
        }
        else
        {
            criteriaInvestment.value(queryValue(SysQuery::valueEmptyString()));
        }
    }

    if (projCostControl.parmProjId())
    {
        criteriaProjId.value(queryValue(projCostControl.parmProjId()));
    }

    if (projCostControl.parmProjWIPId())
    {
        criteriaWIPProjId.value(projCostControl.parmProjWIPId());
    }

    if (projCostControl.parmProjInvoiceProjId())
    {
        criteriaProjInvoiceProjId.value(queryValue(projCostControl.parmProjInvoiceProjId()));
    }

    if (projCostControl.parmProjSorting1())
    {
        criteriaSorting.value(queryValue(projCostControl.parmProjSorting1()));
    }

    if (projCostControl.parmProjSorting2())
    {
        criteriaSorting2.value(queryValue(projCostControl.parmProjSorting2()));
    }

    if (projCostControl.parmProjSorting3())
    {
        criteriaSorting3.value(queryValue(projCostControl.parmProjSorting3()));
    }

    if (projCostControl.parmProjGroupId())
    {
        criteriaGroupId.value(projCostControl.parmProjGroupId());
    }

    if (projCostControl.parmCustomerAccount())
    {
        criteriaCustAccount.value(queryValue(projCostControl.parmCustomerAccount()));
    }

    criteriaProjTransType.value(queryValue(projCostControl.parmProjTransType()));
}

/// <summary>
/// Set the visibility of transaction and item task buttons.
/// </summary>
public void setButtonVisibility()
{
    AccessRight     menuItemAccessRight;
    SecurityRights  securityRights;

    securityRights = SecurityRights::construct();

    // Hour transaction button
    menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(projTransEmpl));

    if (costControlTransCommittedCost.RecId != 0)
    {
        switch (costControlTransCommittedCost.CommittedCostOrig)
        {
            case CommittedCostOrig::PurchaseRequisition:
                menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(PurchReqTable));
                break;

            case CommittedCostOrig::ItemJournal:
                menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(InventJournalTableProject));
                break;

            case CommittedCostOrig::PurchaseOrder:
            case CommittedCostOrig::VendorInvoice:
                menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(PurchTable));
                break;

            case CommittedCostOrig::SalesOrder:
                menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(SalesTable));
                break;

            case CommittedCostOrig::ItemRequirement:
                menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(ProjSalesItemReq));
                break;

            case CommittedCostOrig::Production:
                menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(ProjProdTable));
                break;

            case CommittedCostOrig::BudgetReservation_PSN:
                menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(BudgetReservationEdit_PSN));
                break;
        }
    }

    // Item task button
    if (menuItemAccessRight == AccessRight::NoAccess)
    {
        itemTask.visible(false);
    }
    else
    {
        itemTask.visible(true);
    }
}

void setLinkCriteria()
{
    #define.star('*')

    ProjInvoiceTable            projInvoiceTable;
    ProjTable                   projTable;
    ProjWIPTable                projWIPTable;
    HcmWorker                   hcmWorker;
    ResourceView                resource;
    ResourceCategoryView        resourceCategory;
    InventTable                 inventTable;
    ProjCategory                projCategory;
    ProjCostTrans               projCostTrans;
    ProjEmplTrans               projEmplTrans;
    ProjItemTrans               projItemTrans;
    PurchReqTable               purchReqTable;
    PurchReqLine                purchReqLine;
    PurchTable                  purchTable;
    PurchLine                   purchLine;
    SalesTable                  salesTable;
    SalesLine                   salesLine;
    ProdTable                   prodTable;
    ProdBOM                     prodBOM;
    ProdRoute                   prodRoute;
    ProdCalcTrans               prodCalcTrans;
    BudgetReservationLine_PSN   budgetReservationLine;
    ProjId                      projId;
    ItemId                      itemId;
    str                         tmpQueryStr;
    str                         criteriaLineNumberValue;

    switch (element.args().dataset())
    {
        case(tableNum(ProjWIPTable)):
            projWIPTable = element.args().record();
            tmpQueryStr  = ProjTable::rangeProjId(fieldNum(ProjTable, wipProject), projWIPTable.ProjId);
            criteriaWIPProjId.value(tmpQueryStr);
            break;

        case(tableNum(ProjInvoiceTable)):
            projInvoiceTable = element.args().record();
            criteriaProjInvoiceProjId.value(projInvoiceTable.ProjInvoiceProjId);
            break;

        case(tableNum(ProjTable)):
            projTable   = element.args().record();
            projId      = projTable.ProjId;

            if (ProjTable::child(projId))
            {
                criteriaProjId.value(queryValue(projId) + #star);
            }
            else
            {
                criteriaProjId.value(queryValue(projId));
            }
            break;

        case(tableNum(HcmWorker)):
            hcmWorker   = element.args().record();
            criteriaResourceRecId.value(queryValue(ResourceFacade::findByWorker(hcmWorker.RecId)));
            break;

        case(tableNum(ResourceView)):
            resource = element.args().record();
            criteriaResourceRecId.value(queryValue(resource.RecId));
            break;

        case(tableNum(ResourceCategoryView)):
            resourceCategory = element.args().record();
            criteriaResourceCategoryRecId.value(queryValue(resourceCategory.RecId));
            break;

        case(tableNum(ProjCategory)):
            projCategory = element.args().record();
            criteriaCategoryId.value(projCategory.CategoryId);
            break;

        case(tableNum(InventTable)):
            inventTable   = element.args().record();
            itemId        = inventTable.ItemId;
            criteriaEmplItemId.value(itemId);
            criteriaProjTransType.value(queryValue(ProjTransType::Item));
            break;

        case(tableNum(ProjCostTrans)):
            projCostTrans   = element.args().record();
            transId         = projCostTrans.TransId;
            criteriaTransId.value(transId);
            criteriaProjTransType.value(queryValue(ProjTransType::Cost));
           break;

        case(tableNum(ProjEmplTrans)):
            projEmplTrans   = element.args().record();
            transId         = projEmplTrans.TransIdRef;
            criteriaTransId.value(transId);
            criteriaProjTransType.value(queryValue(ProjTransType::Hour));
            break;

        case(tableNum(ProjItemTrans)):
            projItemTrans   = element.args().record();
            transId         = projItemTrans.ProjTransIdRef;
            criteriaTransId.value(transId);
            criteriaProjTransType.value(queryValue(ProjTransType::Item));
            break;

        case(tableNum(PurchTable)):
            purchTable   = element.args().record();
            criteriaOrigin.value(queryValue(CommittedCostOrig::PurchaseOrder));
            criteriaReferenceId.value(purchTable.PurchId);
            break;

        case(tableNum(PurchLine)):
            purchLine   = element.args().record();
            criteriaSourceDocumentLine.value(queryValue(purchLine.SourceDocumentLine));
            break;

        case(tableNum(PurchReqTable)):
            purchReqTable   = element.args().record();
            criteriaOrigin.value(queryValue(CommittedCostOrig::PurchaseRequisition));
            criteriaReferenceId.value(purchReqTable.PurchReqId);
            break;

        case(tableNum(PurchReqLine)):
            purchReqLine   = element.args().record();
            criteriaSourceDocumentLine.value(queryValue(purchReqLine.SourceDocumentLine));
            break;

        case(tableNum(SalesTable)):
            salesTable   = element.args().record();
            criteriaOrigin.value(queryValue(CommittedCostOrig::SalesOrder));
            criteriaReferenceId.value(salesTable.SalesId);
            criteriaProjTransType.value(queryValue(ProjTransType::Item));
            break;

        case(tableNum(SalesLine)):
            salesLine   = element.args().record();
            if (salesLine.SalesType == SalesType::ItemReq)
            {
                criteriaOrigin.value(queryValue(CommittedCostOrig::ItemRequirement));
            }
            else
            {
                criteriaOrigin.value(queryValue(CommittedCostOrig::SalesOrder));
            }
            criteriaReferenceId.value(salesLine.SalesId);
            criteriaLineNumber.value(queryValue(salesLine.LineNum));
            criteriaProjTransType.value(queryValue(ProjTransType::Item));
            break;

        case(tableNum(ProdTable)):
            prodTable=element.args().record();
            criteriaOrigin.value(queryValue(CommittedCostOrig::Production));
            criteriaReferenceId.value(prodTable.ProdId);
            break;

        case(tableNum(ProdBOM)):
            prodBOM=element.args().record();
            criteriaOrigin.value(queryValue(CommittedCostOrig::Production));
            criteriaTransId.value(prodBOM.ProjTransId);
            criteriaProjTransType.value(queryValue(ProjTransType::Item));
            break;

        case(tableNum(ProdRoute)):
            prodRoute=element.args().record();
            criteriaLineNumberValue='(5==10)';
            while select LineNum from prodCalcTrans
            where prodCalcTrans.IdRefTableId==tableNum(ProdRoute)
            &&    prodCalcTrans.IdRefRecId==prodRoute.RecId
            {
                criteriaLineNumberValue= criteriaLineNumberValue+(strFmt('||(%1==%2)',fieldStr(CostControlTransCommittedCost,LineNum),int2str(Global::real2int(prodCalcTrans.LineNum)-1)));
                criteriaLineNumber.value(criteriaLineNumberValue);
            }
            criteriaOrigin.value(queryValue(CommittedCostOrig::Production));
            criteriaReferenceId.value(prodRoute.ProdId);
            criteriaProjTransType.value(queryValue(ProjTransType::Hour));
            break;

        case(tableNum(BudgetReservationLine_PSN)):
            budgetReservationLine = element.args().record();
            criteriaSourceDocumentLine.value(queryValue(budgetReservationLine.SourceDocumentLine));
            break; 
    }
}

display VendName vendorName(CostControlTransCommittedCost _costControlTransCommittedCost)
{
    DictTable dictTable;

    dictTable = new DictTable(tableNum(VendTable));

    if (dictTable.rights() >= AccessType::View)
    {
        return  VendTable::find(_costControlTransCommittedCost.VendAccount).name();
    }
    else
    {
        return '';
    }
}

/// <summary>
///  Displays the committed cost amount.
/// </summary>
/// <param name="_costControlTransCommittedCost">
///  Record of the committed cost transaction.
/// </param>
/// <returns>
///  The amount based on the quantity and price or quantity.
/// </returns>
display AmountCur costAmount(CostControlTransCommittedCost _costControlTransCommittedCost)
{
    AmountCur  amountCur;

    if (_costControlTransCommittedCost.Qty)
    {
        amountCur = _costControlTransCommittedCost.Amount / _costControlTransCommittedCost.Qty;
    }

    return amountCur;
}

void linkActive()
{
    switch (element.args().dataset())
    {
        case(tableNum(TmpProjCostControl)):
            // If linkActive called again  the criterias are changed and the form should be closed
            if (projCostControl)
            {
                element.close();
            }
            projCostControl       = element.args().parmObject();
            linkProjCostControl   = true;
            break;
    }

    if (element.args().dataset())
    {
        this.query(element.buildQuery());
        element.setLinkCriteria();
    }

    super();
}

public void executeQuery()
{
    switch (selectCommittedCostStatus.selection())
    {
        case ProjCommittedCostStatus::All :
            criteriaStatus.value(SysQuery::valueUnlimited());
            break;

        case ProjCommittedCostStatus::Open :
             criteriaStatus.value(queryValue(NoYes::Yes));
            break;
    }

    if (linkProjCostControl)
    {
        element.linkProjCostControl();
    }

    super();

    if (costControlTransCommittedCost.RecId != 0)
    {
        itemTask.enabled(true);
    }
    else
    {
        itemTask.enabled(false);
    }
}

void clicked()
{
    Args            args;

    args = new Args();
    args.record(costControlTransCommittedCost);
    args.caller(element);

    //Check that a record is selected
    if (costControlTransCommittedCost.RecId != 0)
    {
        switch (costControlTransCommittedCost.CommittedCostOrig)
        {
            case CommittedCostOrig::PurchaseRequisition:
                new MenuFunction(menuitemDisplayStr(PurchReqTable), MenuItemType::Display).run(args);
                break;
            case CommittedCostOrig::ItemJournal:
                args.record(InventJournalTable::find(costControlTransCommittedCost.RefId));
                new MenuFunction(menuitemDisplayStr(InventJournalTableProject), MenuItemType::Display).run(args);
                break;

            case CommittedCostOrig::PurchaseOrder:
                new MenuFunction(menuitemDisplayStr(PurchTable), MenuItemType::Display).run(args);
                break;

            case CommittedCostOrig::SalesOrder:
                new MenuFunction(menuitemDisplayStr(SalesTable), MenuItemType::Display).run(args);
                break;

            case CommittedCostOrig::ItemRequirement:
                new MenuFunction(menuitemDisplayStr(ProjSalesItemReq), MenuItemType::Display).run(args);
                break;

            case CommittedCostOrig::Production:
                new MenuFunction(menuitemDisplayStr(ProjProdTable), MenuItemType::Display).run(args);
                break;
            case CommittedCostOrig::VendorInvoice:
                VendInvoiceInfoTable vendInvoiceInfoTable= VendInvoiceInfoLine::findSourceDocumentLine(costControlTransCommittedCost.SourceDocumentLine).vendInvoiceInfoTable();

                if (vendInvoiceInfoTable.PurchId)
                {
                    costControlTransCommittedCost.RefId = vendInvoiceInfoTable.PurchId;
                    args.record(costControlTransCommittedCost);                            
                    new MenuFunction(menuitemDisplayStr(PurchTable), MenuItemType::Display).run(args);
                }
                else if (costControlTransCommittedCost.Open)
                {
                    args.record(vendInvoiceInfoTable);
                    new MenuFunction(menuItemDisplayStr(VendInvoiceInfoListPage), MenuItemType::Display).run(args);
                }
                else
                {
                    VendInvoiceJour vendInvoiceJour;

                    vendInvoiceJour = VendInvoiceJour::findFromVendTrans(vendInvoiceInfoTable.Num,
                                                                        vendInvoiceInfoTable.TransDate,
                                                                        vendInvoiceInfoTable.InvoiceAccount);

                    args.record(vendInvoiceJour);
                    new MenuFunction(menuItemDisplayStr(VendInvoiceJournal), MenuItemType::Display).run(args);
                }
                break;
            case CommittedCostOrig::BudgetReservation_PSN:
                args.record(BudgetReservationHeader_PSN::findBySourceDocumentHeader(
                    SourceDocumentLine::find(costControlTransCommittedCost.SourceDocumentLine).SourceDocumentHeader));
                new MenuFunction(menuitemDisplayStr(BudgetReservationEdit_PSN), MenuItemType::Display).run(args);
                break;
        }
    }

    super();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    costControlTransCommittedCost_ds.init();
    element.setLinkCriteria();
    costControlTransCommittedCost_ds.executeQuery();

    return ret;
}
