public void addNewCardHolder()
{
    cardTokenize.init(this.getActiveCreditCardProcessorName());
    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        cardTokenize.parmCustAccount(CustAccount);
    }
    if (!cardTokenize.getFromDialog())
    {
        return;
    }

    CreditCardCust cardToken = CreditCard::addCreditCard(cardTokenize.getXmlString(), custAccount, this.getActiveCreditCardProcessorsRecId());
    CreditCardCust::scopeCreditCardToken(cardToken.RecId, RetailTokenScope::Customer);
    cardToken.reread();

    element.args().lookupRecord(cardToken);
    creditCardCust_ds.research(true);
}

public void init()
{
    SecurityRights securityRights;
    TableId tableId = this.args().record() ? this.args().record().TableId : 0;
    CustTable custTableArgs;
    SalesTable salesTableArgs;

    super();

    if (tableId == tableNum(CustTable))
    {
        custTableArgs = this.args().record();
        custAccount = custTableArgs.AccountNum;
    }
    else if (tableId == tableNum(SalesTable))
    {
        salesTableArgs = this.args().record();
        custAccount = salesTableArgs.InvoiceAccount;
    }
    else
    {
        throw error(Error::missingRecord(tableId2name(tableNum(CustTable))));
    }

    securityRights = SecurityRights::construct();

    if (securityRights.tableAccessRight(tableStr(CreditCardCust)) == AccessRight::Delete)
    {
        creditCardCust_ds.allowDelete(true);
    }

    activeCreditCardProcessors = CreditCardProcessors::findActiveProcessor();

    cardTokenize = CreditCardPaymentCardTokenize::construct();

    if (FeatureStateProvider::isFeatureEnabled(RetailRestrictCreditCardUsageFeature::instance()))
    {
        Group_TokenScope.visible(true);
    }
    else
    {
        Group_TokenScope.visible(false);
    }
}

public container parseMonthYear(str _mmyy)
{
    List parts = strSplit(_mmyy, '/');
    ListEnumerator partsEnum = parts.getEnumerator();
    int yearFrom = year(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    int yearTo = year(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())) + #CreditCardExpirationYearsToDisplay;
    int mm;
    int yy;

    if (parts.elements() == 2)
    {
        partsEnum.moveNext();
        mm = str2int(partsEnum.current());
        partsEnum.moveNext();
        yy = str2int(partsEnum.current());
        if (mm >= 1 && mm <= 12 && yy >= yearFrom && yy <= yearTo)
        {
            return [mm, yy];
        }
    }
    return conNull();
}

