public int active()
{
    #CreditCard
    int ok = super();

    // Load credit card data if the card already exists.
    if (creditCardCust)
    {
        cardTokenize.setXmlString(creditCardCust.CardToken, CreditCardProcessors::find(creditCardCust.CreditCardProcessors).Name);

        group_CardType.text(cardTokenize.getPropertyValue(Microsoft.Dynamics.Retail.PaymentSDK.Portable.Constants.PaymentCardProperties::get_CardType()));
        address_Company.text(cardTokenize.getPropertyValue(#CreditCardPaymentCardPropertyName_Company));
        address_Street.text(cardTokenize.getPropertyValue(Microsoft.Dynamics.Retail.PaymentSDK.Portable.Constants.PaymentCardProperties::get_StreetAddress()));
        address_City.text(cardTokenize.getPropertyValue(Microsoft.Dynamics.Retail.PaymentSDK.Portable.Constants.PaymentCardProperties::get_City()));
        address_County.text(cardTokenize.getPropertyValue(#CreditCardPaymentCardPropertyName_County));
        address_State.text(cardTokenize.getPropertyValue(Microsoft.Dynamics.Retail.PaymentSDK.Portable.Constants.PaymentCardProperties::get_State()));
        address_ZipCode.text(cardTokenize.getPropertyValue(Microsoft.Dynamics.Retail.PaymentSDK.Portable.Constants.PaymentCardProperties::get_PostalCode()));
        address_CountryRegionId.text(cardTokenize.getPropertyValue(Microsoft.Dynamics.Retail.PaymentSDK.Portable.Constants.PaymentCardProperties::get_Country()));
        contact_Phone.text(cardTokenize.getPropertyValue(Microsoft.Dynamics.Retail.PaymentSDK.Portable.Constants.PaymentCardProperties::get_Phone()));
        contact_EMail.text(cardTokenize.getPropertyValue(#CreditCardPaymentCardPropertyName_EMail));
    }

    creditCardCust_ds.allowEdit(creditCardCust.CreditCardProcessors == element.getActiveCreditCardProcessorsRecId());
    return ok;
}

